INSERT INTO `aboutus` VALUES (8, '公司简介', '<p><span>南昌为力人力资源服务有限公司，具有独立的法人资格，是国内较早涉足人才派遣业务的人才中介机构。公司凭强大的招聘平台、快捷的网络、依托完整的档案管理系统，以全新的理念和创新的精神打造自己的服务品牌，并致力于建设全方位提供人才派遣服务的专业机构。目前本公司服务涵盖机关、事业、金融、高校、医疗、研究院所、信息IT、服务行业、各类企业，以及驻南昌机构等各类单位。欢迎社会各类性质的企事业单位与我公司合作。在我们的竭诚服务中，为力人力资源服务有限公司为企业提供最好、最专业的服务外包、招聘猎头、员工福利和人事服务等相关人力资源解决方案。</span></p>', '发布', '[\"20200716\\\\c54822eab57deb11cd3afb2c6ea5f85b.jpg\"]', 0, 1594889460, NULL);
INSERT INTO `aboutus` VALUES (11, '企业文化', '<p><b>为力管理理念</b></p><p>以人为本 分工合作&nbsp; 互利共赢&nbsp; 共同发展</p><p><br></p><p><b>为力管理理念</b></p><p>以人为本 分工合作&nbsp; 互利共赢&nbsp; 共同发展</p><p><b><br></b></p><p><b>为力管理理念</b></p><p>以人为本 分工合作&nbsp; 互利共赢&nbsp; 共同发展</p><p><b><br></b></p><p><b>为力管理理念</b></p><p>以人为本 分工合作&nbsp; 互利共赢&nbsp; 共同发展</p><p><b><br></b></p><p><b>为力管理理念</b></p><p>以人为本 分工合作&nbsp; 互利共赢&nbsp; 共同发展</p>', '发布', '[\"20200716\\\\a258d7fb0434817779dad6cb444a70e3.jpg\"]', 0, 1594891455, NULL);
