INSERT INTO `ent_services` VALUES (11, '哈哈哈', NULL, '人事代理', '<p>大萨达撒</p>', 10, '草稿', 1594819547, NULL);
INSERT INTO `ent_services` VALUES (4, '劳务派遣', '20200717\\3f18125306ecd31be54b8649608719c2.jpg', '劳务派遣', '<p><br/></p><div class=\"main center\" style=\"margin-right: auto; margin-left: auto; padding-top: 30px; border: 0px; width: 1200px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><div class=\"fuwu_content\" style=\"margin-top: 70px; border: 0px; width: 1200px;\"><div class=\"content_con_type\" style=\"padding-top: 20px; border: 0px;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">劳务派遣</strong>，又称人力资源派遣，即企事业单位作为实际用工单位根据工作需要与我公司签订《劳务派遣合同》，我公司与员工签订《劳动合同》并派往用工单位工作。用工单位与员工只有使用关系，负责员工的工作管理费用；我公司作为员工的法定雇主，负责员工的人事管理。</span><br/><br/><div style=\"border: 0px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp;<img src=\"http://www.jxwlrl.com/up/pic/201809/28/20180928104814555404358.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; vertical-align: top;\"/></span></div><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">劳务派遣形式</strong></span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 完全派遣</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp; 根据企事业单位需求，由我公司承担一整套员工派遣服务工作，包括人才招募、选拔、培训、劳动关系、薪酬和福利、安全和健康，劳动争议处理等。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 转移派遣</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp; 由企事业单位自行招募、选拔、培训人员，再由我公司与员工签订《劳动合同》，并负责员工的薪酬、福利、绩效评估、处理劳动纠纷等事务。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 短期或项目派遣</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp; 企事业单位为了完成一项生产任务或工作项目需临时用工，由我公司在约定时间内提供人员招聘和劳动合同管理等事务。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp;</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">劳务派遣流程及服务内容</strong></span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 员工招聘：发布招聘信息、组织招聘；</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 员工培训：员工就职前的岗位培训和工作纪律培训；</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 劳动关系：与员工签订劳动合同，劳动合同履行过程中的日常管理，员工离职时为其相关手续；</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 薪酬福利：核算及发放员工的工资和福利待遇，个税申报，办理员工的社会保险和住房公积金的相关手续；</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 工作管理：协助用工单位对员工工作进行管理和考核，落实用工单位的奖惩方案，办理派遣员工的工伤及突发事件的相关事宜；</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 档案服务：根据客户及员工需求，为员工提供统一的存档服务；</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 政策咨询：提供有关人事和劳动管理方面的信息、最新颁布的各地政府政策和规定。</span><br/><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">劳务派遣好处</strong></span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 用人机动灵活；</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 人事管理便捷；</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 人事成本下降；</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 财务成本下降；</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 减少人事（劳动）纠纷；</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">◆ 解决用人编制问题。</span></div></div></div><p><br/></p>', 30, '发布', 1594816436, NULL);
INSERT INTO `ent_services` VALUES (5, '服务外包', NULL, '服务外包', '<p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">人力资源服务外包（英文，Human Resource Service Process Outsourcing），指的是企业为了降低人力成本，实现效率最大化，将人力资源事务中非核心部分的工作全部或部分委托人才服务专业机构管（办）理。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp; &nbsp; &nbsp;一般来说，人力资源外包包括人事服务外包、人力（劳务）外包和人力资源专业管理外包三种。目前，国外人力资源管理人员与员工的比例通常是1比100，而在国内这个比例却在1比30左右。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><img src=\"http://www.jxwlrl.com/up/pic/201809/28/20180928104552470237068.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; vertical-align: top;\"/><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">人力资源业务外包是指企业为更好地开展人力资源管理活动，把部分业务外包出去，借助于外界优秀的专业化技能加以整合，以达到降低成本、提高服务质量和更专注于人力资源核心业务的目的。对我国企业来说，我国企业的外包起步稍晚，但是发展速度同样非常快。从20世纪90年代初开始，东南沿海地区的许多企业纷纷开展外包，从而增强了企业的灵活性，提高了对市场的反应速度，降低了企业的经营风险。由于人力资源是企业中重要的资源，并且已日益凸显出其在企业价值链中的重要作用，因此，做好人力资源管理工作具有非常重要的意义.。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>人力资源外包行业在经济发达地区发展迅速。在经济发达地区，不乏苏州、义乌、顺德等这样的制造基地，有一定规模和数量的企业，市场劳务工的缺口较大，人力资源外包市场发展前景广阔。对于企业来说，选择何种用工方式，如何降低企业成本，增强核心竞争力是重要课题。不少企业的人力资源部常为人才招聘奔波，耗费了大量的时间和精力，成本高但效果却不理想。高级人才的选聘因缺乏人脉资源而困难重重，初级人才的招聘则太浪费时间。随着中国客户对外包服务认知度的提高，外包服务的需求也将大幅增加，特别是经济发达地区的管理者，思想更开放，能够尝试并接纳新的管理模式，这对人力资源外包行业的发展起到了不可忽略的推动作用。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>随着整个行业的发展，外资企业日益增长的人才服务需求推动着中国人力资源服务行业发展，中国人力资源服务企业积极改变服务模式，实现从提供“单一的人力资源服务”向“全方位的人力资源服务”的转变，作为中国一家人力资源外包服务企业，华仕人力资源服务有限公司的服务领域开始围绕主体业务向多角度延伸，逐渐涉足管理咨询、调查研究等更多领域。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>中国作为世界上人口最多的国家，具备着丰富的人力资源。改革开放三十年来，随着中国经济社会的蓬勃发展，人力资源已经成为国家经济建设的第一资源，人力资源服务行业的发展前景广阔，并将朝着专业化、产业化和国际化的方向发展。<br/></span></p><p><br/></p>', 20, '发布', 1594816455, NULL);
INSERT INTO `ent_services` VALUES (6, '人事代理', NULL, '人事代理', '<p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">人事代理</strong>，是一种人力资源外包形式，是企业根据需求将一项或多项人力资源管理工作或职能外包出去，由专业的第三方代理。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">人事代理是与社会主义市场经济体制相配套的新型人事管理模式，是指政府人事部门所属人才服务机构受单位或个人委托，运用社会化服务方式和现代科技手段，按照一定的法律程序和政策规定，代办有关人事业务。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><img src=\"http://www.jxwlrl.com/up/pic/201809/28/201809281056351460200334.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; vertical-align: top;\"/><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">人事代理服务内容：</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">1、政策咨询与规划。向委托代理单位提供国家人事工作方面的法律、法规和政策规定的咨询服务；协助委托单位进行人事规划设计，建立新型人事管理制度；帮助委托单位解决人事工作中的问题。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>2、人才招聘引进。根据委托单位工作和发展对人才的具体要求，代拟和代发人才招聘启事，组织报名、考试、考核。素质测评工作，提出初选名单。根据单位的特殊需求，向省内外、国外招聘引进人才。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>3、应届毕业生人事代理。为委托单位提供应届毕业生就业政策咨询，申报应届大<br/>中专毕业生需求计划，为接收的大中专毕业生按转档案，办理转正定级手续。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>4、代办专业技术职称申报评审手续。为委托单位专业技术人员和管理人员办理专业技术职务资格初走及资格考试报名、晋升推荐手续，组建相应评审委员会，负责对部分专业技术职务资格评审与评议推荐工作。<br/>5、人事档案管理。按照有关政策规定，管理委托单位专业技术人员、管理人员的人事档案、考绩档案，为委托代理人员保留原有身份、计算工龄、调整档案工资、接转党团员组织关系、办理出国（境）政审手续、代办集体户口落户、出具以档案材料为依据的有关证明。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>6、办理在职流动人员人事关系按转手续。根据单位工作需要，为聘用人员接转人事关系、党团员组织关系等；为合同期满流动者，办理人事关系转出手续。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>7、聘用合同鉴证。按照有关规定，办理聘用专业技术人员、管理人员的合同鉴证。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>8、 协调专业技术人员流动争议。受人才流动争议仲裁机构的委托，受理专业技术人员、管理人员流动争议申请，进行调查、取证，根据有关规定进行凋解或提交仲裁。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>9、 开展岗位及专业技能培训。根据单位对人员素质和技能的要求，进行岗位知识及专业技能培训，为单位代培中。长期专业人才。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>10、 按照有关协议，向社会推荐委托单位的辞聘、解聘人员重新就业。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>11、 代办失业、养老保险。按照国家和我省有关规定，为委托单位代管人员代办失业、养老保险。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>12、 为委托单位承办其他人事管理事宜。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>13、对个人委托的人事代理，可参照上述人事代理内容提供代理服务。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">服务对象：</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">（一）各类非国有（三资、集体、民营、乡企、私营等）单位和其他自愿委托的各类国有企事业单位。<br/>（二）下列专业技术人员和管理人员（即各类流动人员）应 该进行人事代理。<br/>（1）辞职、辞退和解除聘用合同的人员；<br/>（2）各类事务所和其他社会中介组织聘用的人员；<br/>（3）各类非国有单位聘用的人员；<br/>（4）自费大中专毕业和毕业研究生；<br/>（5）自谋职业的军队转业干部；<br/>（6）自费出国留学人员和留学回国人员；<br/>（7）其他各类流动人员。<br/><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">服务流程：</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><br/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">1、与客户企业沟通服务需求和要求。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">2、双方企业交流相关情况，并提供各自营业执照、机构代码证及企业概况等相关经营合法资料。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">3、根据客户企业的相关需求和要求，制定服务方案，并与客户企业作相关沟通</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><img src=\"file://https://gss1.bdstatic.com/-vo3dsag_xi4khgkpowk1hf6hhy/baike/s=220/sign=6fdd8bef1e178a82ca3c78a2c602737f/908fa0ec08fa513dcb6c1dfd3d6d55fbb3fbd9cb.jpg\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; vertical-align: top;\"/><span style=\"margin: 0px; padding: 0px; border: 0px;\">人事代理所。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">4、双方沟通达成一致意见，并约定具体服务事项、服务内容、双方的权利及义务、解除及终止协议情形等相关条款，签订《人事服务合同》。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">5、人事代理机构提供专业对口服务人员，由需客户企业确定联系人配合，就相关服务事务合同规定开展服务工作。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">6、需由人事代理机构代为签订员工《劳动合同》的，客户企业还需向人事代理机构提供客户企业一方相关的劳动人事规章制度和员工手册，以及员工的相关工作岗位、薪酬、福利、奖金及其一系列必要的雇用信息。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">7、委托人事代理机构进行员工《劳动合同》解除、终止或续签等合同管理的，人事代理机构将负责提供与此相关的法律咨询，并代为进行相关程序办理。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/></span></p><p><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\">8、人事代理机构负责管理客户企业雇用员工的信息，建立雇佣员工个人信息档案并作即时的更新。&nbsp;</span></p><p><br/></p>', 640, '发布', 1594816480, NULL);
INSERT INTO `ent_services` VALUES (7, '灵活用工', '20200717\\529bed66c9d795b76f961f187633096a.jpg', '灵活用工', '<p><br/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp;&nbsp; &nbsp; &nbsp; 灵活用工</strong>等同于“<strong style=\"margin: 0px; padding: 0px; border: 0px;\">灵活派遣</strong>(Flexible Staffing)”，是人才派遣服务领域的成长型产品，它由派遣公司承担全方位的法定雇主责任，在派遣人数确定、派遣周期、派遣人才的筛选方面都非常灵活的一种用工形式。&quot;用人不管人，管人不用人&quot;是派遣制用工形式。派遣单位招聘和管理职工而不使用职工，用人单位使用职工但不招聘和管理职工。从法律关系来看，劳动者和劳务派遣单位签署劳动合同，劳务派遣单位和用工单位签署劳务派遣协议，涉及劳动者、用人单位（即劳务派遣单位）以及用工单位三方主体。</span><span style=\"margin: 0px; padding: 0px; border: 0px;\"></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><img src=\"http://www.jxwlrl.com/up/pic/201809/28/20180928110412895528932.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; vertical-align: top;\"/><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp; &nbsp; &nbsp; &nbsp;1、作为用人单位认为：这种劳动用工形式，灵活机动，好处多多。这种用工形式解决了编制限制，大大降低用人单位的成本，有利于提高效益和效率。而且可以有效避免劳动者不确定性导致的各种风险，用人单位什么时候缺人、缺多少、缺哪些人，只需照单索取。用人单位只需要把支票一开，其它的事情，如工资发放、福利发放、各项保险缴纳等，统统由劳务派遣机构搞定，省工、省时、省心。对于派遣来的员工，用人单位根据个人表现，能够很快发现一些能人，加以重点培养，或者开点小灶，可以将这些劳务工转化为用人单位的长期员工，实现了人力资源使用的可持续发展。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>　　2、劳务派遣机构认为：作为专门从事劳务中介的“第三者”，一方面要保护劳动者的切身利益，另一方面要维护企业的权益，最重要的是这个机构要“有利可图”。派遣机构负责人才信息的搜寻、筛选和培训。用人单位为这种中间流程的便利买单。人才派遣机构将零散无序的劳动者整理、打包、外派，变无序为有序、变无组织为有组织，减少了劳动者外出务工的盲目性，为务工者提供了一条便捷、快速、稳妥、安全的就业通道。</span></p><p><br/></p><p><br/></p>', 70, '发布', 1594816505, NULL);
INSERT INTO `ent_services` VALUES (8, '人才招聘', '20200717\\197f252e19da9824c598757400d759ba.jpg', '人才招聘', '<p><br/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp; &nbsp; &nbsp; &nbsp;招聘流程外包，是一种以客户为导向，以项目为建制的招聘解决方案服务，是着眼于公司在较长的招聘周期内大规模招聘需求提供的定制化解决方案。</span><span style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp;</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp; &nbsp; &nbsp; 招聘流程外包（RPO ）服务属于人力资源外包（HRO）的一种方式，在这种服务中，企业外包的是内部招聘的整个流程，所谓整个流程是指从确定职位描述，到分析用人理念、职位需求、与用人部门负责人沟通，筛选简历，人才测评，面试到录用通知，直至候选人报到的所有环节。也就是说，RPO 可以提供的是“起点到终点(End to End)”的一站式服务。招聘流程外包(RPO)是指一个企业将整个招聘流程交付外部机构来完成，它涉及从人力发掘到最后的招聘录取工作的方方面面，是个集成化的过程。简单说来，RPO就是定义一个招聘流程，并将这个招聘流程交给一个外部公司来完成，当然这个公司必须具有相应的资历和经验，并按这个招聘流程来为委托公司招聘所需人才。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><br/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.jxwlrl.com/up/pic/201809/28/201809281113051762837103.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; vertical-align: top;\"/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">招聘流程外包可以提高招聘质量、缩短填补职位空缺的时间、改善整个行政流程、管理核心业务指标的汇报以及削减总体成本，许多企业都开始利用RPO完成招聘任务。大幅削减成本和改善招聘质量可以提高招聘流程的成效与效率。RPO是指企业将全部或部分招聘需求外包给专业公司来完成。此类专业服务公司的功能相当于企业内部的招聘部门，负责向客户提供必要的技能、工具和技术。&nbsp;</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">服务范围：</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">1)固定周期内，有较大量的职位需求的企业。<br/>2)新建项目团队，招聘压力大的企业。<br/>3)招聘主管对候选人的预期与实际情况不一致，导致招聘工作很难展开的企业；<br/>4)需要到另一个城市开设新工厂或者运营中心，但是自身的人才储备或招聘渠道有限，内部招聘部门压力过大的企业；<br/>5)需要优化内部面试和测评流程的企业。<br/>6)需要在二三线城市开展招聘，但是二三线城市候选人员各方面能力不均衡，选拔困难的企业。<br/>7)猎头公司访寻候选人的工作效率不能满足目前需求的企业。<br/>8)规模不大，暂无计划设立内部招聘部门的企业。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/><strong style=\"margin: 0px; padding: 0px; border: 0px;\">服务内容：</strong><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">1、<strong style=\"margin: 0px; padding: 0px; border: 0px;\">企业代理招聘：</strong>企业招聘需求量大，招聘人员不足，以及新组建企业的人员招聘，省去企业招聘广告、发布信息、面试等成本和时间。该服务适用于技术类职位和人事行政财务类职位，以及部分主管级职位，但招聘难度很大的职位属于猎头职位范畴</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>2、<strong style=\"margin: 0px; padding: 0px; border: 0px;\">应届生校园招聘外包：</strong>大型企业进行应届生的校园招聘工作，时间紧，准备工作烦琐，可将前期准备工作如信息发布、招聘信息宣传推广、校园宣讲、简历接收和筛选、第一轮面试和笔试等工作外包给人才公司，企业人力资源部门可专注于少量候选学生的专业和素质考察以及最终面试录用，缓解了人力资源部的时间压力，提高了招聘的效率。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/>3、<strong style=\"margin: 0px; padding: 0px; border: 0px;\">招聘流程整合咨询：</strong>可帮助中大型企业进行招聘流程优化，招聘渠道整合，重建招聘体系，培训和辅导企业招聘人员提升招聘技能，根据企业发展规划构建企业人才梯队并辅导实施，从而提升人才对于企业竞争优势的支持<br/></span></p><p><br/></p><p><br/></p>', 60, '发布', 1594816523, NULL);
INSERT INTO `ent_services` VALUES (9, '人才培训', '20200717\\ce61913aabecccc5b0c5700ae258ba13.jpg', '人才培训', '<p><br/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">人才培训</strong>是指对人才进行教育、培训的过程。被选拔的人才一般都需经过培养训练，才能成为各种职业和岗位要求的专门人才。培养人才的形式有多种，除了在各级各类学校中进行系统教育的进修外，还可采取业余教育，脱产或不脱产的培训班、研讨班等形式，充分利用成人教育、业余教育、电化教育等条件，提倡并鼓励自学成才。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><img src=\"http://www.jxwlrl.com/up/pic/201809/28/201809281124131791670000.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; vertical-align: top;\"/><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">服务项目</strong></span></p><p><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">招考委托&nbsp;</strong>（招聘、招考、笔试、面试流程化服务）&nbsp;&nbsp;</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">入职培训</strong>（岗前培训、新员工融入拓展培训）&nbsp;&nbsp;</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\">技能培训</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\">&nbsp; &nbsp; &nbsp; &nbsp;1) 茶艺培训、花艺培训、香道培训、礼仪培训、家政服务培训、拓展培训及以上各类培训师资班；</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\">&nbsp; &nbsp; &nbsp; &nbsp;2) 安全生产培训、食品安全培训、特种作业培训、特种设备操作培训；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\">行业培训</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\">&nbsp; &nbsp; &nbsp; &nbsp; l 电力系统培训（优质服务、班组长管理、安全管理、经营管理、技能培训等）</span><br/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">学历提升（函授本科、在职研究生）</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">企业内训（股权、投融资、领导力、执行力、市场营销、商务礼仪、户外拓展、教练技术、人力资源、九型人格等）</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/></span></p><p><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\">职业技能认证（人力资源师、企业培训师、职业指导师、理财规划师、心理咨询师、物流师、电子商务师、创业咨询师、健康管理师、营销师等）</span></p><p><br/></p><p><br/></p>', 10, '发布', 1594816541, NULL);
INSERT INTO `ent_services` VALUES (10, '代交社保', '20200717\\7bd3ced2dffc5025330890eea91f306f.jpg', '代交社保', '<p><br/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">代缴社保是社保代理服务的主要内容，指代理公司为企业或个人提供代办或缴纳社会保险的服务，是人事代理服务中众多项目之一。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><br/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">代缴社保作为一种中介服务，随着各地相关配套政策落实到位，越来越受到社会关注，并已成为一种产业。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><br/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">社会保险主要包括五种：</strong><br/>1、养老保险：即劳动者因年老丧失劳动能力时，在养老期间发给生活费，以及生活方面给以照顾。<br/>2、医疗保险：即劳动者在患病期间在医疗、护理方面的保障措施。<br/>3、失业保险：即劳动者在失业期间的生活费、医疗费的给付以及转业培训、生产自救及 职业介绍等保障措施。<br/>4、工伤保险：即劳动者因工负伤，暂时或永久丧失劳动能力后的工资收入补偿，也是对因工负伤劳动者的医疗护理和生活照顾措施。<br/>5、生育保险：女职工在生育期间保障，以及家属可以享受的待遇。<br/></span></p><div style=\"border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal; text-align: center;\"><img src=\"http://www.jxwlrl.com/up/pic/201809/28/201809281221111146335447.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; vertical-align: top;\"/></div><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><br/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">服务内容：</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">1.社保开户，公积金开户；</span><span style=\"margin: 0px; padding: 0px; border: 0px;\">社保</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">2.社保账户的增减员，五险一金的缴纳；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">3.生育报销、工伤保险报销、公积金提取等业务；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">4.解答五险一金相关问题；</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">5.其他五险一金相关的业务办理。&nbsp;</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">服务对象：</strong></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">（1）外资企业和异地企业驻本地分支机构；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">（2）各商业连锁加盟企业常驻经营网点；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">（3）不设人事管理岗位的中小企业；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">（4）没有企业为其缴纳社保的个体工商户；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">（5）自由职业者；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">（6）因工作变动，暂时没有公司为其缴纳社保的人员；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">（7）其他符合政策规定的单位和个人。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><br/></p><p><strong style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\">代交社保的好处：</strong><span style=\"color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">简答来说，代缴社保对企业的帮助是：提高效率，规避风险，节省成本，促进发展。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">具体而言：</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">1、帮助人力资源部门从繁重的重复性事务中解脱，专注于核心的战略性工作，从而提升人力资源管理的高度和核心竞争力；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">2、提供接触新管理技术的机会，提高响应的速度与效率；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">3、规范操作，有效遏制随意性的薪资、员工管理，对管理工作的规范性、公正性起到促进作用；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">4、降低成本，舒缓资金压力，克服企业很多的规模经济弱点，在国内由于劳工权利意识的提高、就业安全体系和劳动法令的普及，人事直接间接费用及外围成本不断地增加，人力资源管理业务外包可以降低企业风险，摆脱杂务干扰，最终引导企业专心经营核心资源，发展核心竞争优势；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">5、避免大量投资于人才所带来的不确定风险；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">6、简化流程，节省时间，提高员工满意度；</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">7、不会因社保专员的专业性不强操作不当引发纠纷。</span></span></p><p><br/></p><p><br/></p>', 0, '发布', 1594816569, NULL);
INSERT INTO `ent_services` VALUES (12, '大萨达', '20200717\\a86a94eb99327d96c47e8010ebc5a780.jpg', '服务外包', '<p>大大萨达萨达</p>', 0, '草稿', 1594969951, NULL);

-- ----------------------------
-- Table structure for job_info
