DROP TABLE IF EXISTS `job_info`;
CREATE TABLE `job_info`  (
  `id` int(155) UNSIGNED NOT NULL AUTO_INCREMENT,
  `station` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `department` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `degree` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `salary` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `people` int(25) NULL DEFAULT NULL,
  `response` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `demand` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `benefit` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `add_time` int(155) NULL DEFAULT NULL,
  `publish` varchar(5) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `delete_time` int(155) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 16 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

