-- Records of job_news
-- ----------------------------
INSERT INTO `job_news` VALUES (10, '测试一', '职场快讯', '<p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">或许贫困的枷锁套着你，或许你没有<a href=\"http://undefined\">朋友</a>，整日形单影只，而且你急切地盼望停止抱怨</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　或许贫困的枷锁套着你，或许你没有朋友，整日形单影只，而且你急切地盼望着压在自己身上的沉重负担能够减轻；然而，重负依旧，你似乎掉进了黑暗的深渊。或许你不停抱怨，哀叹自己的<a href=\"http://undefined\">命运</a>；你指责自己出生的环境、你的<a href=\"http://undefined\">父母</a>、你的老板，或者那种让你遭受贫困与艰辛，却让他人<a href=\"http://undefined\">享受</a>富足与轻松的不公正的上苍。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　停止你的抱怨，消除你的<a href=\"http://undefined\">烦恼</a>；你所指责的这些对象中，没有一个是造成你身处贫困境地的因。因就在你自身内，一切都需要你自己来补救。你怨天尤人，只会让自己处境依旧，而且它还足以说明，你缺乏一种<a href=\"http://undefined\">信念</a>，而这种信念就是<a href=\"http://undefined\">努力</a>与进步的基石。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　在讲究法则的宇宙中，没有怨天尤人者的立足之地，而且担忧实际上相当于<a href=\"http://undefined\">灵魂</a>自杀。倘若你没有一个正确的<a href=\"http://undefined\">心态</a>，那么你只会让锁链把自己束缚的更紧，使自己一直<a href=\"http://undefined\">生活</a>在黑暗中。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　改变自己对生活的消极<a href=\"http://undefined\">态度</a>，你的外部<a href=\"http://undefined\">人生</a>也将随之改变。用信念及知识武装自己，让自己能够获取更好的处境，更大的机会。首先，确保你用最佳的方式利用你所<a href=\"http://undefined\">拥有</a>的一切。在<a href=\"http://undefined\">现实</a>生活中，你应该脚踏实地，大处着眼，小处着手，不要好高骛远。同时，你应该及时总结以前的教训，否则你则有可能重蹈覆辙。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　就像学校的<a href=\"http://undefined\">孩子</a>们在学习下一课之前，必须首先掌握现在所学的课程一样，你在向着你渴望的更加丰硕的成果努力之前，必须牢牢地把握你已经拥有的一切。圣经中不少<a href=\"http://undefined\">故事</a>，都蕴含着这个道理。如果我们误用、忽略我们所拥有的，或者使我们所拥有的渐渐退化，那么不管它多么微不足道，我们也会由于那种表现我们不配拥有它的做法，而<a href=\"http://undefined\">失去</a>它。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　使你的环境更优雅</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　或许你居住在一个小屋中，四周的环境并不整洁。你渴望有一个更大的、卫生一点的住所。那么接下来，为了使自己将来配得上这样的住所，你必须首先把自己的小屋建成一个小天堂。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　把屋内保持得干干净净、一尘不染。尽自己所能，让小屋给人一种<a href=\"http://undefined\">美丽</a>、<a href=\"http://undefined\">温馨</a>的<a href=\"http://undefined\">感觉</a>。精心去做简单的饭菜，整齐地摆放并不昂贵的餐具。如果你买不起一卷华丽的地毯，那么就让你的小屋充满<a href=\"http://undefined\">微笑</a>与欢迎，用良言做钉，靠<a href=\"http://undefined\">耐心</a>这把锤子，把它牢牢地固定下来。这张地毯，在阳光的照射下会永不褪色，而且经久耐用。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　你要想法设法使目前的环境更优雅，这样你将能够超越它们，高于它们的要求。到了合适的<a href=\"http://undefined\">时间</a>，你会搬到更好的房子中，生活在更加优雅的环境里。其实，这更好的房子与更加优雅的环境一直都在<a href=\"http://undefined\">等待</a>着你，而且通过昔日的努力你已使自己完全有资格接受它们。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　或许你渴望有更多的时间去思想，去做别的事情，而且觉得你的劳动强度太大，劳动时间太长。遇到这种情形，你应该看一看自己是否充分利用了每一点可以利用的时间。如果你在浪费着现有的时间，那么你对更多时间的渴求也将变得毫无意义，因为那样只会让你变得更加懒惰，你的时间观念也会更加淡薄。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　貌似坏事实际上可让人得益的幸事</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　甚至贫困及缺少时间与休闲，并非你所想象的坏事，而且倘若它们妨碍你进步的话，那也是因为你用自己的缺点把它们包裹起来。你在它们中看到的坏的一面，实际上是你自身内坏的一面。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　你应当通过自己的努力，比较全面、透彻地认识到，你在塑造自己的心态时，你就是自己命运的制造者。借助自律的能量，你会越来越多地认识到这一点，最终你将明白，这些所谓的坏事可以被转化为幸事。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　你随后将利用你的贫困看，来培养你的耐心、<a href=\"http://undefined\">希望</a>与<a href=\"http://undefined\">勇气</a>。你缺少时间，那么这完全可以促使你充分利用现有的每一点珍贵时间，久而久之，你就可以培养出当机立断、雷厉风行的作风。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　如同杂草丛生的土地了长着最美丽的花，在你贫困的黑土地里，也绽放着美丽的人生之花。哪里有困难需要克服，哪里有不尽如人意的境况需要改善，哪里就有人的美德散发着芳香。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　或许你受雇于一位专横无礼的男主人或女主人，你感觉自己时常被他或她粗暴地对待。那么把它也看作是你人生的必修课。用温和及<a href=\"http://undefined\">宽容</a>回报你雇主的不友好。锻炼自己持久的耐心与自制力。利用不利的外部条件，获得思想及精神的力量，逐步化逆境为顺境，通过自己无声的举动与无形的影响，你教育了你的雇主，而且帮助他或她渐渐为自己的行为感到羞耻，与此同时，把自己提高到一个新的精神境界。有了这种新的精神境界，你就能够步入崭新的、更加亲和的环境中。</p><p><br/></p>', 10, '发布', 1594277674, NULL);
INSERT INTO `job_news` VALUES (11, '测试二', '职场快讯', '<p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　一个人，越是有福气，越是<a href=\"http://undefined\">善良</a>；一个人，越是<a href=\"http://undefined\">喜欢</a>做善事，越是有福气。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　<a href=\"http://undefined\">生活</a>中，一个人的善事，做的越多越有福气，坑人害人的事，不做更有福气。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　其实一个人的福气，都是从<a href=\"http://undefined\">做人</a>做事开始，该做的事就做，不该做的事就不做。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　有人帮助你，是你的福气；而你去帮助别人，便是积攒福气。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　有福气的人，都有善良的心，乐善好施，喜欢助人为乐，用自己的善举，去感染他人；用自己的善心，去<a href=\"http://undefined\">宽容</a>他人。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　一个人的福气，是多是少，是深是厚，都是靠自己的善良修来的，人的一举一动都是修行，你的善行善举，修的都是福气。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　一个人的福气，不是生来就具备的，是善良的结果，是付出的回报，人善良才有福气。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　人的福气，都是自己一辈子攒来的，善有善的福气，恶有恶的报应，所以，人要多做好事，多积福气。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　善良的人，做善事，也要有原则，别把善良给了伪善的人，否则对恶是一种纵容。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　善良的人，才能广结善缘，善缘多了，自会有人记住你的好，在你<a href=\"http://undefined\">人生</a>落难时，自然会有贵人相助，而你也因为你的善良，得到该有的福气，所有，人越是善良，越是有福气，越有福气，做的善事越多。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　每个人所的福气，不会凭空而来，也不会凭空消失，都是生活里的滴滴点点，积攒下来的善良，做好事，才能行好运，可遇不可求。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　<a href=\"http://undefined\">命运</a>从来不会偏袒任何人，福气都是垂怜那些心地善良，认真生活的人。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　<a href=\"http://undefined\">内心</a>善良的人，慈眉善目，用善良的眼光看世界，世界才更<a href=\"http://undefined\">美好</a>，用善良的心，去做人做事，才一生问心无愧，活的心安。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　人生在世，每个人都<a href=\"http://undefined\">希望</a>自己，就是那个很有福气的人，不需要操劳，就可以活的很<a href=\"http://undefined\">幸福</a>。同时，也给自己的家人带来幸福，然而，想<a href=\"http://undefined\">拥有</a>这样的福气，都离不开经常做善事。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　人重在善良，美在<a href=\"http://undefined\">心灵</a>，你的善良就是你最大的福气，懂<a href=\"http://undefined\">感恩</a>，懂<a href=\"http://undefined\">珍惜</a>。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　善良的人，也许会吃亏，也许会上当，但是，人而好善，福虽未至，祸其远矣，善良的人，终究不吃亏。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　善良的人，做人清清白白，受人尊敬，得上天眷顾，人活一世，善良做人，踏实一生。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　心存善良，多一些宽容；心存美好，少一些恼事；做一个善良，真诚的人，是一生最大的福气。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　爱人者，人恒爱之；敬人者，人恒敬之；持善心，行善事，善行善举，越做越有福气。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　我们做的每一件善事，最终都回到自己的身上，成为我们人生中的福气。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　生而为人，不管是有钱没钱，善良最重要；为人处世，不管嘴笨嘴甜，善良都是做人的本钱。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　人活一世，都要坦坦荡荡，活的光明磊落，不欺骗，不敷衍，做这个正直的人。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　善良的人，只会帮人，不会害人；只会助人向善，不会助纣为虐；只会用善良，感化他人，<a href=\"http://undefined\">温暖</a>世界。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　一个人越善良，越<a href=\"http://undefined\">幸运</a>；越善良，越有福气；越善良，越幸福；越善良，越有好报；笑到最后的人，都是善良的人。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　善良的人，是<a href=\"http://undefined\">人性</a>里最有力量的温暖；善良的人，更是一种远见；因为善良的人，在帮助别人，也是在帮助自己。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　善良从来都不会错，善良做事，才能<a href=\"http://undefined\">成功</a>；善良做人，才能赢得未来；善良才是对自己最好的投资。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　善良的人，为人向善，福报自然会来，人只要保持自己的善良，多做善事，多行善事所付出的，岁月都会回馈你，善行善事，越做越有福气。</p><p><br/></p>', 0, '发布', 1594277701, NULL);
INSERT INTO `job_news` VALUES (12, '测试三', '职场快讯', '<p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">十句真言</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　第一句：</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　<a href=\"http://undefined\">做人</a>，一定要知恩图报！别忘了曾经帮助过你的恩人，别<a href=\"http://undefined\">伤害</a>经常指引你的贵人，否则，你的<a href=\"http://undefined\">朋友</a>会越来越少，你的<a href=\"http://undefined\">人生</a>会越过越难。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　第二句：</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　别人怎么看你，怎么说你，都是他们的事，和你毫无关系，你要怎么活，怎么过，也和他们毫无关系，做好你自己，无需去在意。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　第三句：</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　当你到了一定的年龄，你就会明白，聪明是一种天赋，而<a href=\"http://undefined\">善良</a>是一种选择，善良永远比聪明难做！</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　第四句：</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　不联系，不代表忘记，但是不闻不问一定是疏远了，变淡了，彼此<a href=\"http://undefined\">沉默</a>太久，最后连问候都需要斟酌，连主动都需要<a href=\"http://undefined\">勇气</a>！</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　第五句：</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　语言，是最具有影响力的的东西，也是最具有杀伤力的武器，所以永远不要嘲讽和用比较绝的话去伤害那些爱你的人。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　第六句：</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　有时候，<a href=\"http://undefined\">等待</a>会等来<a href=\"http://undefined\">遗憾</a>，所谓的来日方长，其实就是挥手告别。机会<a href=\"http://undefined\">错过</a>了，没有下一次，<a href=\"http://undefined\">真情</a>错过了，就是一辈子！</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　第七句：</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　不纠缠，不抱怨，不强求，不计算，用顺其自然的<a href=\"http://undefined\">心态</a>，过轻松自在的<a href=\"http://undefined\">生活</a>，用心甘情愿的<a href=\"http://undefined\">态度</a>，过随遇而安的人生。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　第八句：</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　有些人和事，不是别人错了，而是自己看错了，自己眼瞎别怪别人，其实都是自己的问题。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　第九句：</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　有时候，我们原谅了别人，却依然不<a href=\"http://undefined\">快乐</a>，我们放下了仇恨，却依然不<a href=\"http://undefined\">开心</a>，那是因为，我们忘了原谅自己，放过自己。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　第十句：</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　最好的<a href=\"http://undefined\">幸福</a>，就是活着！以自己<a href=\"http://undefined\">喜欢</a>的方式活着，不要管别人如何对待你、评价你，健康快乐才是重点，才大于一切！</p><p><br/></p>', 20, '发布', 1594277725, NULL);
INSERT INTO `job_news` VALUES (13, '测试四', '面试宝典', '<table width=\"100%\" border=\"0\" style=\"font-family: &quot;Microsoft YaHei&quot;; margin-top: 8px;\"><tbody><tr><td class=\"article\" style=\"font-size: 14px; line-height: 1.5;\"><div id=\"wenzhangziti\" class=\"article 364311\" style=\"line-height: 1.5;\"><p>对有些事，需要睁一只眼，闭一只眼，这是智慧；可是对有些事，不能睁一只，闭一只眼，这是原则。</p><p>　　遇见是两个人的事，分开却是一个人的决议；遇见是一个开端，分开却是为了遇见下一个分开。这是一个盛行分开的世界，但是我们都不擅长辞别。<br/>　　———米兰·昆德拉</p><p>　　别人都在关注你飞得高不高、快不快时，只有他在关心你飞得累不累，这是<a href=\"http://undefined\">朋友</a>。别人关注你钱多钱少、官大官小时，只有她在关心你是胖了瘦了，那是<a href=\"http://undefined\">母亲</a>。<br/>　　———《黑暗中的舞者》台词</p><p>　　男孩<a href=\"http://undefined\">喜欢</a>上了<a href=\"http://undefined\">女孩</a>，他向她<a href=\"http://undefined\">表白</a>，女孩回绝了，她说：我整整比你大一岁。男孩说：我一个月大时，你十三个月大。你是我的十三倍。我两个月时，你十四个月。你是我的七倍。我一岁时，你两岁，你是我的两倍。只需你愿意和我永远在一同，我们总在渐渐接近。<br/>　　———<a href=\"http://undefined\">美丽</a>的情话</p><p>　　我问佛：如果遇到了可以爱的人，却又怕不能把握怎样办？佛曰：留人间多少爱，迎浮世千重变；和有<a href=\"http://undefined\">情人</a>，做<a href=\"http://undefined\">快乐</a>事，别问是劫是缘。<br/>　　———患得患失，连眼前的快乐都会<a href=\"http://undefined\">失去</a></p><p>　　任何一种环境或一个人，初次见面就预见到<a href=\"http://undefined\">分手</a>的隐痛时，你必定爱上他了。<br/>　　———黄永玉《沿着塞纳河到翡冷翠》</p><p>　　要想得到别人的爱，首先要自己爱自己。因为，漂亮的<a href=\"http://undefined\">女人</a>像宝石，但是花有几日红，岁月不饶人。而智慧的女人像宝藏，男人愿意一辈子去挖掘。<br/>　　———马云说女人</p><p>　　<a href=\"http://undefined\">生命</a>是厨房的；收入是商场的；奖金是化妆品的；财产是没有的；成绩是上司的；身体是男人的；只有雀斑和皱纹是自己的。<br/>　　———女人的<a href=\"http://undefined\">悲哀</a></p><p>　　一直对发型不满意的人有一个共同点：不肯承认这是脸的问题。<br/>　　———活在自我欺骗中的人</p><p>　　<a href=\"http://undefined\">励志</a>有两种方法，一种是看别人<a href=\"http://undefined\">成功</a>，一种是看别人<a href=\"http://undefined\">失败</a>。<br/>　　———这样“励志”</p><p>　　一个人的涵养，不在心平气和时，而是心浮气躁时；一个人的理性，不在风平浪静时，而是众声喧哗时；一个人的慈悲，不在居高临下时，而是人微言轻时；情侣间的尊重，不在闲情逸致时，而是观点相左时；夫妻间的恩爱，不在花前月下时，而是大难临头时。<br/>　　———陈道明</p></div></td></tr><tr><td style=\"font-size: 13px; line-height: 1.5;\"></td></tr><tr align=\"center\"><td colspan=\"2\" class=\"prenext\" style=\"font-size: 13px; line-height: 1.5; padding-bottom: 6px;\"><div class=\"endad\" id=\"gad4\" style=\"line-height: 1.5; text-align: left; padding-bottom: 17px;\"><iframe id=\"iframe295372_0\" src=\"https://pos.baidu.com/ycqm?conwid=468&conhei=60&rtbid=2056748&rdid=9223372032564375000&dc=2&exps=110011&psi=301ab959c4fac830ae85973de55cca7d&di=295372&dri=0&dis=0&dai=0&ps=1054x316&enu=encoding&ant=0&dcb=___adblockplus_&dtm=HTML_POST&dvi=0.0&dci=-1&dpt=none&tsr=0&tpr=1594277744452&ti=%E5%AF%B9%E6%9C%89%E4%BA%9B%E4%BA%8B%EF%BC%8C%E9%9C%80%E8%A6%81%E7%9D%81%E4%B8%80%E5%8F%AA%E7%9C%BC%E9%97%AD%E4%B8%80%E5%8F%AA%E7%9C%BC%EF%BC%8C%E8%BF%99%E6%98%AF%E6%99%BA%E6%85%A7%20-%E7%BE%8E%E6%96%87%E6%95%85%E4%BA%8B-%E6%95%A3%E6%96%87%E6%97%A5%E5%BF%97%E9%9A%8F%E7%AC%94-%20%E6%96%87%E7%AB%A0%E9%98%85%E8%AF%BB%E7%BD%91&ari=2&ver=0702&dbv=2&drs=1&pcs=1519x763&pss=1519x1281&cfv=0&cpl=3&chi=1&cce=true&cec=GBK&tlm=1581224196&prot=2&rw=763&ltu=http%3A%2F%2Fwww.duwenzhang.com%2Fwenzhang%2Fjingdianwenzhang%2Fyuju%2F20161208%2F364311.html&ltr=http%3A%2F%2Fwww.duwenzhang.com%2Fwenzhang%2Fjingdianwenzhang%2F&lcr=https%3A%2F%2Fwww.baidu.com%2Flink%3Furl%3D7I66aVcW_eeHYfbedsy5erAYdY0GTzU1VK-9zN2XaHHWz8YeBeEllWCwe5jLDxohW9Odcf9wx1K6ai6SOJMWHa%26wd%3D%26eqid%3Df7437d0e0009b78f000000065f06bf0a&ecd=1&uc=1536x834&pis=-1x-1&sr=1536x864&tcn=1594277744&qn=f8728493162a00a9&tt=1594277744445.12.327.327\" frameborder=\"0\" style=\"border-width: 0px; border-style: initial; vertical-align: bottom; margin: 0px; width: 468px; height: 60px;\"></iframe></div></td></tr></tbody></table><p><br/></p>', 20, '发布', 1594277752, NULL);
INSERT INTO `job_news` VALUES (14, '测试五', '招生简章', '<p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　吃罢晚饭，正要收拾碗筷，楼下突然传来激烈的争吵声。仔细听听，原来是两口子准备<a href=\"http://undefined\">离婚</a>。为<a href=\"http://undefined\">孩子</a>，房子，车子和存款达不成最终分割的统一意见而大战马路。丝毫不顾及路人的眼光和哭的撕心裂肺的孩子。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 离婚，在这个物欲日益膨胀的<a href=\"http://undefined\">社会</a>，已经不是什么新鲜事。离婚的理由也是五花八门，只要<a href=\"http://undefined\">婚姻</a>中有一方，想要退出婚姻的围城，围城的墙壁上随时都可以找到一个可以钻出去的洞口。一个芝麻绿豆的事态，也会被人用心良苦的大肆宣扬，无限的扩大，直到另一方妥协，被吹大的肥皂泡泡才会破灭，才会息事宁人。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 无形的洪荒之力，把应有的<a href=\"http://undefined\">责任</a>心冲打的溃不成军，霓虹灯飘渺的光影下，一颗心越来越虚浮。<a href=\"http://undefined\">忍耐</a>的底线越来越高，<a href=\"http://undefined\">包容</a>的尺度越来越小，关怀的甜点越来越少。越来越多的与时俱进的学会了自私自利的<a href=\"http://undefined\">享受</a>，把自己高高在上的放在心中无可替代的位置。爱，成了一个人的独角戏，不再分享与共。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 庄严神圣的大红色结婚证书，渐渐的褪去了它的终身制和不可侵犯的<a href=\"http://undefined\">尊严</a>，变成了可以随意调换的商品，不再用“从一而终”的<a href=\"http://undefined\">心态</a>去呵护它，守卫它。闪婚，闪离，踏着快节奏的<a href=\"http://undefined\">生活</a>旋律，走着快节奏的步伐，新生代的名词在一闪过后，铺天盖地的流行起来。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 一只刚出炉的面包，在适宜的温度下有三天的寿命，若放在高温潮湿的地方，能隔夜不发霉，就很不错了。采摘下一只健康的苹果，在妥善的保存下要半年才能脱水干瘪或者是腐烂。若放在桌上置若罔闻，顶多采摘下来一个月，外观就让人不忍直视。它们寿命的长短，不在乎是否劣质，而是没有正确的，稳妥的，用心的去呵护。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　&nbsp;<a href=\"http://undefined\">爱情</a>也一样，需要妥善的保管才能让它的<a href=\"http://undefined\">生命</a>力发挥到极致。携手走入婚姻围城的俩个人，不管初遇时的心动多么激烈，若是不好好的用心供养爱情的玫瑰花园，保鲜期会提前，会在凋零之后发霉腐烂。不管贫富与否，爱情里从来不缺少<a href=\"http://undefined\">浪漫</a>，缺少的是把激情正确的转化为<a href=\"http://undefined\">亲情</a>的方向，让两颗心，不仅仅是你装着我，我装着你，还默契的装着我们对未来的打算和珍藏一路共有的记忆。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 婚姻，不是用来捆绑<a href=\"http://undefined\">感情</a>的，而是责任的升级。有感情的人不一定非要结婚，但结了婚就一定要负起责任。组建起这个<a href=\"http://undefined\">家庭</a>的男人和<a href=\"http://undefined\">女人</a>，不再是<a href=\"http://undefined\">单纯</a>的个体，而是既能<a href=\"http://undefined\">自由</a>活动又能互相约束的结合体。同在一个屋檐下，共同打造一个家，而这个“家”质量的好坏，能不能长久的使用，和遭遇风雨时能不能得到妥善的维护，是要两个人齐心协力去付出的。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 好的婚姻，都是用心经营出来的。一份呵护打造一份和谐，一份容忍收获一份安稳，一份付出得到一份明媚。经营的阳光和绚，鸟语花香的家，不管涉入尘世的心有多疲惫，迈进家门的那一刻，轻松宜人的氛围都会把<a href=\"http://undefined\">心灵</a>上的尘埃轻轻的拂去。一句暖心的话，一杯<a href=\"http://undefined\">喜欢</a>的茶，在日复一日的简单中，让纯真的感情愈发的浓厚。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　&nbsp;<a href=\"http://undefined\">温暖</a>心灵的不是暖气，拂去烦躁的不是空调，而是一份懂得与体贴。能够拴住一颗心的不是容貌与身材，也不是地位与身价，而是另一颗心的内涵。两情相悦，四壁透风的草房，也会被内在的<a href=\"http://undefined\">幸福</a>填满。家是心灵的港湾，是<a href=\"http://undefined\">灵魂</a>栖息的地方。不需要奢华，却一定要舒适。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 婚姻的灵魂是感情，一个没有好好维系感情的婚姻就是一个空壳，无论装潢多么的奢侈华丽，都会在风雨中不堪一击。每天，细腻的感受爱人的一颦一笑，适时的调节自己<a href=\"http://undefined\">心情</a>，转换自己的思想。用心的接受身边人的悲与欢，对与错的同时，也坦诚的对身边人敞开自己心扉，把自己的苦与乐，阴与晴用恰当的方式倾诉与爱人。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 交流，是让爱情长久不可或缺的方式之一。有些顾虑，说出来，心结会轻轻的打开。有些矛盾，说到明处，干戈就会化为玉帛。有些苦衷，说出来，紧蹙的眉头就会云淡风轻。爱人也是人，不会未卜先知，也不会能掐会算，有些话，不说，就<a href=\"http://undefined\">失去</a>了让TA懂得的机会。即便不懂，TA还可以问，你还可以解释。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 夫妻关系和任何一种关系都不一样。手足之情，<a href=\"http://undefined\">朋友</a>之情，都是真挚的，甚至我们可以拿命去换，可真的某天利益当头，还是会掂量下轻重，考虑一下是否可行。夫妻的利益不分孰重孰轻，一荣俱荣，一损俱损，没有彼此之分，没有胜败之说，确切的说，彼此之间的任何事态，都没有什么利益或是损失可以是单对某一方的。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 这样的两个人，没有理由不相互敞<a href=\"http://undefined\">开心</a>扉，掏出肺腑。在为<a href=\"http://undefined\">事业</a>打拼的空闲时，在烟熏火燎的罅隙里，背靠着背，对着一窗新月，把柔软的心敞开，听听爱人的心，也让爱人听听自己的心。用自己温柔的体贴缝补爱人闯荡在外留下的伤口，而自己在人际中的憋屈也让爱人用TA的细腻温情融化。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 最好的婚姻，不是郎才女貌，门当户对，而是无才无貌，门不当户不对的两个人，彼此在乎，彼此<a href=\"http://undefined\">珍惜</a>，彼此忍让，彼此奉献，因为心中真挚的爱，粗茶淡饭才会在唇齿间留香，粗布衣衫才能穿出眼中旖旎的风景。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 能够走进婚姻的殿堂，无外乎是一见倾心或是日久生情。只是那个当初怦然心动的人在柴米油盐中黯然了色彩。当青蛙王子天天为生活奔波的时候，他的帅气和体贴难免打些折扣；当白雪公主时时为生活操碎了心的时候，她的贤淑与温柔难免遗失些许。不要抱怨婚前与婚后的TA判若两人，能够改变对方的因素大部分是自己造成的。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 作为一个男人若能顶天立地的为爱人撑起一片晴空，用关怀和呵护让她无忧无虑如少女般生活在<a href=\"http://undefined\">温馨</a>的摇篮里，被爱情滋润的她会更加<a href=\"http://undefined\">可爱</a>，比婚前有过之而无不及。没有哪个女人愿意做怨妇，心情舒畅的女人会把锅碗瓢盆的协奏曲弹奏的很美妙，虽比不了天籁之音，却也能让人流连忘返。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 做为一个女人若能用一个贤妻良母的温婉为爱人解去后顾之忧，用体贴和善解人意让他能够<a href=\"http://undefined\">放手</a>自己的事业，被爱情浇灌的他会更加<a href=\"http://undefined\">成熟</a>稳重，用全身心去为家打拼。没有哪个男人愿意把家庭当作战场，温暖舒适的小窝会让他乐此不彼的为家人系上围裙，用一粥一饭诱惑爱人的味蕾，温暖爱人的心扉。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 生活的快节奏，社会的高强度压力，免不了会有心理上的压抑需要用发泄来缓解。而发泄的方式有很多种，有人酗酒，有人玩命工作，有人大发雷霆，喋喋不休的抱怨，只有少数明智的人，才会对着爱人，娓娓道出自己的<a href=\"http://undefined\">感觉</a>。甘苦与共的另一半，也有自己的独立的思想，有时候TA会无法接受你的缓解的方式。勺子碰到锅沿时，恰当的退一步，给枕边人一份包容，一份体谅和一分钟的解释。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 细腻的爱情会擦去在尘世中跋涉而蒙尘的心，会修复在风吹雨打中道道伤痕，会营养在外应酬疲惫而枯燥的心。好的爱情是婚姻的润滑剂，在不断的注入下，婚姻会永远的和谐<a href=\"http://undefined\">美好</a>。“百年修得同船渡，千年修得共枕眠。”虽然我不<a href=\"http://undefined\">相信</a>前世来生的说法，相信人只是个一次性的肉体，用完之后，就会回归自然的垃圾场。但在千千万万的人之中，能够遇见真的不容易，应该用生命珍惜这份来之不易的<a href=\"http://undefined\">缘分</a>。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　 两个人在一起，同在一个屋檐下，并驾齐驱，没有高低贵贱之分，没有聪明笨傻之分。在日久的相濡以沫里，<a href=\"http://undefined\">习惯</a>慢慢融合，<a href=\"http://undefined\">性格</a>慢慢互补，爱的激情慢慢升华成血脉相连的亲情。爱与情，不仅仅是左手牵右手的浪漫，更是息息相通的心与肺；不仅仅是心甘情愿的做另一半的拐杖，更愿意做TA的眼，TA的嘴，TA的一切的一切……</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; font-size: 14px; white-space: normal;\">　　文：董金梅 笔名：飘落红尘笑如烟</p><p><br/></p>', 0, '发布', 1594277777, NULL);
INSERT INTO `job_news` VALUES (15, '教大学生求职如何防止掉“坑”里？', '职场快讯', '<div class=\"content_con_type\" style=\"padding-top: 20px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\">现在是大学生出来实习找工作的高峰期，而大学生由于没有职场经验，可以说是完全白纸一张，不知道职场上有诸多坑，稍不留神就容易掉坑里；比如，大学生毕业找工作误入传销窝点这样的大坑里，让自己痛苦，更让家人痛苦；为预防这样的诸多类似事件的发生，南昌为力人力资源服务有限公司在此给大家支招。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\"><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\">&nbsp; &nbsp; &nbsp;&nbsp;<a href=\"http://undefined\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none;\">南昌为力人力资源</a>是一家以劳务派遣、人力资源服务为主的服务型公司，立足于大学生市场，为大学生提供就业的机会，所以更加理解大学生的处境，更想保护好大学生的正确求职之路。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; text-align: center;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\"><img src=\"http://www.jxwlrl.com/up/pic/201811/15/20181115101431421378389.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; vertical-align: top;\"/><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\">&nbsp; &nbsp;<strong style=\"margin: 0px; padding: 0px; border: 0px;\">(一)、虚假广告陷阱</strong><br/><br/>一些用人单位在招聘会上为了招到条件较好的毕业生，会夸大或隐瞒自己的某些情况。比如：在发布招聘信息时，往往故意扩大用人单位规模和岗位数量，进行虚假宣传;或者把招聘职位写得冠冕堂皇，不是“经理”就是“总监”，但实际上却只是“办事员”、“业务员”，根本没有广告上写的那么诱人。<br/><br/><strong style=\"margin: 0px; padding: 0px; border: 0px;\">(二)、色情陷阱</strong><br/><br/>一些用人单位利用招聘、面试等侵犯学生。有一些招聘广告上称招聘男女公关人员，月薪上千或上万，令一些涉世不深的毕业生掉入陷阱。所谓“男女公关”实则是从事性服务;所谓“高薪”实则是从事性服务时客人所给的小费。面对这样的问题或遇到这样的情况，学生一定要提高警惕，不可随便跳入这种陷阱。<br/><br/><strong style=\"margin: 0px; padding: 0px; border: 0px;\">(三)、传销陷阱</strong><br/><br/>所谓传销，本是指生产企业不通过店铺销售，而由传销员将本企业产品直接销售给消费者的经营方式。<br/><br/>该经营方式受到国家的严令禁止。现在的传销者首选对象常常是急于挣钱的打工者特别是刚刚毕业的学生，他们通过各种渠道得到欲骗对象的电话后，便打着同乡、同学、亲戚等幌子，以帮忙找工作为由，以高薪为诱饵，因人而异，投其所好，骗求职者去进行非法传销活动。求职者一旦进入陷阱，便限制人身自由，被迫从事传销，要么交3000-4000元入门费，要么花3000-4000元购买传销产品作为入门条件。传销组织者还采取扣留身份证、控制通讯工具、监视等手段不让受骗者离开，强迫他们联系亲友前来，或者寄钱寄物从中牟利。<br/><br/><strong style=\"margin: 0px; padding: 0px; border: 0px;\">(四)、协议陷阱</strong><br/><br/>就业协议是明确毕业生、用人单位在毕业生就业择业过程中权利和义务的书面协议。就业协议一经签订，对双方都具有约束力。按照有关规定，就业协议不能代替劳动合同或聘用合同，这样就可能在毕业生和用人单位之间产生纠纷。常见的毕业生签就业协议过程中遇到的陷阱又分为以下几种：<br/><br/>1. 用人单位不与毕业生签订就业协议书<br/><br/>2.用人单位不跟应聘者签订劳动合同<br/><br/>3.用人单位不将承诺写入合同<br/><br/>4. 用人单位与毕业生签订“霸王合同”<br/><br/><strong style=\"margin: 0px; padding: 0px; border: 0px;\">(五)、试用期陷阱</strong><br/><br/>试用期，就是劳动关系的试验阶段，但决非是用人单位对劳动者的单方“试用”。我们这里所说的试用期，是指用人单位和劳动者为了相互了解而选择、约定的考察期。<br/><br/>一般来说，单位用人有试用期是正常的，试用期的薪水一般都不高，等到转正之后，薪水会有较大幅度提高。很多公司为了使用廉价劳动力，抓住毕业生急于找工作的心理，堂而皇之地打出试用期的牌子，看起来非常规范，待试用期一过，以种种理由告诉求职者不符合录用条件就将其解聘了。这样的公司不断地炒人，毕业生永远不会成为正式员工。<br/><br/><strong style=\"margin: 0px; padding: 0px; border: 0px;\">(六)收费陷阱</strong><br/><br/>当前，在就业市场中，一些用人单位利用毕业生求职心切，设立各种名目向毕业生收取各种不合理费用，如风险抵押金、违约金、培训费等。一些单位可能规模不大，薪水不高，但是开出了一些诱人的条件。比如说，在某大中城市工作，或者能解决这些大中城市的户口问题。希望留在大中城市工作的学生很容易被这样的条件迷惑。双方谈得差不多了，单位又表示，为了增加双方的信任，学生在工作之前必须交押金。等学生交完押金，工作一段时间后，单位的有关人员就表示，聘用之初说定的工作岗位要有些调整，可能把你派到偏僻地区或冷僻部门，而这些地方是学生肯定不愿意去的：单位算准了学生不愿意去，就说学生不服从单位安排，也是主动毁约放弃这个岗位，这样，学生交的押金自然收不回来。<br/><br/><strong style=\"margin: 0px; padding: 0px; border: 0px;\">(七)薪酬陷阱</strong><br/><br/>所谓薪酬陷阱，是指用人单位在招聘时以优厚的待遇吸引前来求职的毕业生，等到其正式上班时，招聘时的承诺则以种种理由不予兑现;或是针对薪酬中的一些不确定收入，进行虚假或模糊的承诺，最终不能兑现;或者“缩水兑现”。<br/><br/><strong style=\"margin: 0px; padding: 0px; border: 0px;\">(八)智力陷阱</strong><br/><br/>有些单位按程序假装对应聘毕业生进行面试，再进行笔试。在面试、笔试时，把本单位遇到的问题以考察的形式要求前来应聘者作答或设计，待毕业生利用专业优势完成其承担的项目后，再找出各种理由推辞，结果无一人被录用，用人单位却将应聘者的劳动果实据为己有，使毕业生陷入智力陷阱。<br/><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\">&nbsp; 以上支招，不仅希望即将出来的大学生能好好的阅读，谨防上当受骗，对于很多步入职场的求职者都需要好好的保护自己，安全的步入职场，为社会和企业创造价值。</span></p></div><p><br/></p>', 20, '发布', 1594879549, NULL);
INSERT INTO `job_news` VALUES (16, '9条经典忠告 让你少奋斗5年', '职场快讯', '<div class=\"content_con_type\" style=\"padding-top: 20px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">第一：不要认为停留在心灵的舒适区域内是可以原谅的。<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　每个人都有一个舒适区域，在这个区域内是很自我的，不愿意被打扰，不愿意被push，不愿意和陌生的面孔交谈，不愿意被人指责，不愿意按照规定的时限做事，不愿意主动的去关心别人，不愿意去思考别人还有什么没有想到。这在学生时代是很容易被理解的，有时候这样的同学还跟“冷酷”“个性”这些字眼沾边，算作是褒义。然而相反，在工作之后，你要极力改变这一现状。否则，你会很快变成鸡尾酒会上唯一没有人理睬的对象，或是很快因为压力而内分泌失调。但是，如果你能很快打破之前学生期所处的舒适区域，比别人更快的处理好业务、人际、舆论之间的关系，那就能很快的脱颖而出。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">第二：不要把“好像”：“有人会……”：“大概”：“晚些时候”：“或者”：“说不定”之类放在嘴边。尤其是和上级谈论工作的时候。<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　我十分痛恨听到的一句话是：“我晚些时候会把这个文件发给所有的人”；因为这往往预示着我必须时刻提醒他不要忘记。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">　第三：不要拖延工作<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　很多人喜欢在学习和玩耍之间先选择后者，然后在最后时间一次性赶工把考试要复习的东西突击完成。但是在工作中请不要养成这样的习惯，因为工作是永远做不完的，容不得你“突击”。又或者，当你在徘徊和彷徨如何实施的时候，你的领导已经看不下去，自己去做了。这是一个危险的信号。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">第四：不要认为理论上可以实施就大功告成了！<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　这点太重要了，往往当真正实施的人开始做了才会发现计划完全等于鬼话。如果不亲自实践，做计划的人会早晚被实施的鄙视。永远需要提升自己的办实事的能力，而不是空谈。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">第五：不要让别人等你<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　在任何情况下都不要让别人放下手头的工作来等你。在大学中可能只是同寝室的人的几句半开玩笑的抱怨，在工作上很可能导致你的潜在合作伙伴的丢失。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　你在做一个工作的同时要知道别人的进度，而永远不要落后。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">第六：不要认为细节不重要<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　在大学里，往往做事粗枝大叶，看看差不多就行了。相反，在企业里管理的精髓就在于将简单的事情做到细节。一个慌忙寻找保险箱钥匙的动作就很有可能丧失你晋升财务主管的机会。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">第七：不要表现得消极，仅仅因为你所做的事情不是你的兴趣所在。<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　很显然，在学生时代，当做到自己喜欢的时候，我们会pay200%的精力去创造，但如果是枯燥的事务，我们便懒得理睬，最好能有办法应付过去。但在工作上，80%你所做的事情都是繁琐而看似机械的，如果仅仅为此而表现的闷闷不乐，那么你会郁闷更久。要知道你的上司已经为这个项目够烦恼了，你还想让他看到你的表情吗？　　</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">第八：绝对不要把改善工作能力仅寄托在公司培训上。<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　人绝对不可能经过一次培训就脱胎换骨。相反，集体培训上学到的东西往往是最用不上的信息。就像食堂烧大锅菜一样，总没有你最想吃的菜，因为这样做容易，并且不容易得罪人。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　很多学生很看重所选的公司有没有培训，这说明，你不但不知道这个公司做什么，你甚至不知道怎样学习这些技能。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">　第九：不要推卸责任<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　推卸责任是害怕的条件反射。不要认为别人看不出这点。</span></div>', 0, '发布', 1594879568, NULL);
INSERT INTO `job_news` VALUES (17, '职业转型的正确姿势：行动在先 认识在后', '职场快讯', '<div class=\"content_con_type\" style=\"padding-top: 20px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">今天，许多步入职业中期的专业人士都面临一个迫切的问题：我此刻是否仍在正轨，是否应该改变方向？在过去十年中，进行重大职业转换的人群大规模涌现，这还不包括那些考虑转换职业的人。然而，一位渴望改变，行动却依旧如故的人与一位敢想敢做，重新寻找自我实现的人之间是有区别的，而且这种区别可能会出乎你的意料。看看下面的故事：</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　苏珊·方丹曾是一家大型咨询公司的合伙人和战略部门主管。她毅然决定要与无法实现自我的过去一刀两断。但是这位管理咨询专家并没有想清楚未来的方向。当一位老客户给她一个顶级战略工作的机会时，她欣然接受了。她已为改变摩拳擦掌，而且这份工作看上去十分诱人，实在不容错过。然而，结果却令她大失所望——虽然这是个极其完美的工作，用她的话说，可以写成一份“完美衔接的后MBA简历”，但它在各个方面都与原先的工作完全相同，根本无法带来她所渴望的改变。就职仅两周后，她就意识到自己犯了一个很大的错误。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　加里·麦肯锡正值人生转折期，他的个人生活一波三折，工作中也遭遇挫折。从商学院毕业后，这位投资银行家和顾问按常规进入了一家蓝筹股公司工作，等候机会，希望寻找到他“真正的兴趣”。现在，他决定积极地做一次职业选择。为了不出差错，加里做了所有“正确”的事。他首先咨询了职业心理专家，对方给了他一系列测试，帮助他了解自己的兴趣和价值观。他不断和猎头、朋友及家人交流职业转换的话题，并广泛阅读这一领域的畅销书。但用他自己的话说，以上方法无一奏效。加里对可能的行业和公司进行了调研，列出了两个清单：感兴趣的职业方向和已尝试过的工作。但一年后，可行的转型方案还是遥不可及。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　当我思考这些人和过去几年间几十个研究对象的经历时，有一点显而易见：真正进行方向性的改变知易行难。这并非因为管理者或专业人士不愿接受改变。恰恰相反，许多人对重塑自我都有过认真严肃的尝试，并不惜冒着工作和个人风险，在此领域花费大量的时间与精力。虽然付出巨大，他们仍陷在错误的职业中，不能实现个人潜能和职业抱负。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　许多研究者及职业顾问在察觉这种困境后，将问题归根于人的基本动机：我们惧怕改变，准备不足，不愿做出牺牲。但通过深入研究，我得出一个完全不同的结论：人们遭遇失败，最可能的原因在于他们沿用了完全错误的方法。事实上，正是传统观点让转换职业的人们裹足不前。问题的根源不在于动机，而在于方法。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">职业转型的全新路径<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　当我们开始不仅质疑今天所处的工作或组织是否合适，也开始反思自己对未来的设想时，那些曾经的职业搜索方式就不再奏效了。但这并不意味着我们只能听天由命，任由不可控因素主导——不论是一场迫使我们重新定义人生重心的生活危机，或一份出乎意料的应聘邀请。在传统方法之外，有一个与其相去甚远的思路和方法。加里和苏珊及很多我观察到的许多成功实现职业转型的人士，均无一例外地使用过此方法，我将它称为“实验学习”法。在转型期间，我们“可能的自我”变化无常，唯一能推动改变的方法是将这些可能的身份付诸实践，进行精心尝试，直到具备足够的实战经历，再做出更多决定性选择。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">1、实验 学习<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　职业身份是一个复合概念，它包括：工作类型、人际关系、所在的组织以及职业故事（你如何走到今天、为何从事此份工作）。因此，重塑职业身份是逐步调整以上所有方面。这是渐进式和试探性的调整，从表面看过程可能无序可循。但事实上，通过实验、发现和调整，这是个非常有条理的过程，对任何寻求职业更新的人来说都适用。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　“实验学习”方法认为，对抗不确定性和安于现状的唯一方法，是将未来的可选方案变得更生动、形象、可行。我们的旧身份来自实践。同样，通过精心实验、社交转型和赋予正在经历的变化以意义，就可以在实践层面重新定义这些身份。这三个方面是这种颠覆传统的职业转型路径的核心内容，看似随机且行为无序。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">　2、精心实验<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　尝试转换职业跑道时最常犯的一个错误，就是迟迟不迈出第一步。这是个致命的错误，因为发现梦想职业的唯一方法是行动和尝试。大部分人在踏入未知之前会迟疑不决，这不难理解。但是，我们必须勇于尝试这些幻想。否则，它们永远只是幻想。我发现大部分人创造的新职业身份最初只是副业，往往以工作之外的尝试和周末项目得以实现。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　精心实验指的就是创造这些副业项目。它最大的好处是我们可以在不牺牲现有工作的前提下，有限度地尝试新的专业角色；或避免我们过快跳入一个全新领域。几乎所有我观察的成功转型者，在进入新职业前都已与其进行较长并深入的接触。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　精心实验的方法有很多种。下定决心要进行多种尝试，苏珊选择了从事原有领域的自由职业工作，并为慈善机构做免费的公众服务以帮助她度过困难期。通过这份工作，她开始结识新朋友，他们将她介绍到可支付薪酬的慈善咨询机构。她开始逐渐深入非营利机构，这是她从未设想过的职业。不仅如此，她发现自己非常喜欢从事自由职业。今天，她就职于英国最大的一家专注于慈善机构的顾问公司，她说：“我唯一的希望就是自己不会再犯当初的错误——在探索真正想做的工作前就跳槽。”</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　学习新领域的课程、参与培训或考取证书是精心实验的另一种实践方法。在我的研究中，很多人通过管理课程和和休假时间提高新技能。这些假期使人收获良多，帮助我们脱离每日的常规工作，结识新人并参与不同的活动。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">3、社交转型<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　提起自己的公司，想想这样的员工反馈有多普遍：“公司里没有我的榜样。”步入职业中期，我们不仅希望改变工作本身，更希望我们的职场关系可以变得更有意义、更具启发性。改变社交关系指的是尝试寻找那些帮助我们发现新自我并获得成长的人。根据我的观察，大部分成功完成职业转型的人都获得了他人的指点，或加入新的专业组织作为职业转换的缓冲地带。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　寻找新工作需要在熟悉的人际圈外构建新的人际网络。通过向外延伸，可以获得新想法和工作机会。加里成功利用校友和公司网络即是一个例子。他因为公司一位前雇员的帮助而获得现在公司的临时项目，他本人并不直接认识这位介绍人。但促使他下决心更换职业的原因是因为，他将获得与一位前辈共事、并直接向对方学习的机会。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　面对变化和不确定性，人们很自然会向熟悉的同事寻求安慰。哈里斯·罗伯茨是一家大型医疗保健公司的法规部主管。当他转向多年的导师艾尔弗雷德寻求建议时，犯了一个典型的错误：对方的过度呵护使他成为一名不自信的学徒，这抑制了他的成长空间。哈里斯能从这种“共生”关系中走出，得益于一位在业内会议上结识的朋友。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　这位朋友叫格里，是一家公司的创始人，随后雇用哈里斯成为首席运营官。当时，他因法规问题向哈里斯寻求建议。最终，他们建立起一种非正式的咨询关系。因为格里，哈里斯找到了一位能看见他具有总经理潜能的人，对方给了他一种亲密无间、互相独立的工作关系：“这与我之前和艾尔弗雷德之间的关系是天壤之别。”哈里斯说，“这不是一种父子式的关系。格里知道我需要学习的领域——与创造性金融及资金筹资有关的内容，但他也需要从我身上学习东西。他不知道怎样运营一家公司，但这是我的专长。他找到我，希望我告诉他发展组织和奠定根基需要做的工作。我觉得自己可以通过格里学到很多，相比我和艾尔弗雷德的关系，这是一种更成熟、更专业的关系。”</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　如果我们希望与过去一刀两断，就必须勇于开拓新的人际关系，这不仅是为了获得工作机会，而是因为，陌生人往往能帮我们看见自己的未来。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">4、赋予意义<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　深陷困惑，不知何去何从的时候，很多人都希望出现一件事澄清所有困惑，将我们的挣扎变成清晰的轨迹。一位试图放弃从医的医生朱利奥·冈萨雷斯这样说：“我一直在等待一次顿悟——某天我一觉醒来，出现一位怜悯的天使告诉我该怎样做。”职业身份尝试的第三步——赋予意义的时刻是创造改变的契机：为那些瞬时、枯燥的事件赋予特别的意义，将它们编织成关于我们未来的故事。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　每个成功进行职业转换的人都经历过真相揭晓的时刻。约翰·亚历山大希望成为一名作家，我在前文对他有所提及，他的真相时刻来自他对一位占星师的即兴拜访。令他吃惊的是，这位占星师对他说的第一句话是：“我很庆幸在过去两、三年没见到你。你正因两股力量互相角力而经历一次痛苦的内心拔河。一边希望获得稳定、经济富足和社会地位，另一边却渴望艺术创造，或许像作家或剧团经理。你可能暗自希望能两者兼顾。我想告诉你，这不过是妄想。”</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　另一位职业转换人士是一位就职于新兴高科技公司的管理主管，她越来越不满意自己的工作，说“有一天，我丈夫突然问我：‘你开心吗？如果开心最好。但你看上去不是很开心。’他的问题让我反思自己到底在做什么。”</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　我们常常以为，类似故事是引发职业转型的起源。但其实这些时刻常是更换职业的结果，而不是原因。我采访很多人以后，吃惊地发现这种认识仅在经历多种磨难后才会发生，它常出现于职场转折的晚期。这些具有决定意义的时机没有催生改变，而是帮助人们理解早已在发生的改变，赋予其意义。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　诱因事件不止使我们从常规中惊醒，也是我们改写自己故事的线索。将人生整理成一个有条不紊的故事，是职业转换过程中很微妙却极富挑战性的工作。在职业转折初期，我们只有一堆凌乱的想法，惴惴不安，也没有任何值得讲述的故事。好故事可以通过不断讲述而丰富、完善，甚至在它还未全部完成之前。例如，约翰并没有对自己拜访占星师的行为感到尴尬，而是将这个故事告诉了身边所有人，甚至将其发表于报纸专栏。他越靠近自己希望寻找的创作道路，这个故事就变得越真实、它就越少会引发诸如“你怎么会想从事这种工作”的反馈。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　如果将自己所寻找的事物以及旧我与新我之间的连接公之于众，就可以表明自己的动机，从而得到更多外界的支持。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　</span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">　5、脚下的路<br/></strong><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　大部分人都清楚知道自己想要逃离的处境：单一的、缺乏真实感或乏味透顶的工作、让人麻木的公司政治以及被工作侵占的个人生活。寻找真正匹配的转型方案，如同寻找人生使命一样，绝不是一蹴而就的事。这个过程需要时间、坚持和艰辛的付出。但是单凭努力是远远不够的，使用恰当的方法并将其付诸实践的技巧不可或缺。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(42, 42, 42); font-family: Simsun; font-size: medium; line-height: 25px; background-color: rgb(245, 250, 255);\">　　这种全新的职业转型路径挑战着常理。它要求我们将时间和精力更多地付诸于行动，而非反思；立刻行动，而不是制定计划。它要我们放弃寻找一个按部就班的计划，而学会接受曲线路径。但这条看似神秘叵测的大马士革之路，却是一条所有人都能通过“行动学习法”（learning-by-doing）找到的道路。这条路的起点就是行动。</span></div>', 0, '发布', 1594879587, NULL);
INSERT INTO `job_news` VALUES (18, '95后毕业生：如何迈好职场第一步？', '职场快讯', '<p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">站上风口 VS. 稳扎稳打？</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">站在风口上，猪都会飞。近年来，VR、金融科技、短视频、区块链等各类概念满天飞，让人眼花缭乱，似乎只要踏对节拍瞬间就会走上人生巅峰，这对年轻一代的择业观产生了一定的影响。</span><br/><br/><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">风口论非常形象地展示了一些适时出现的新产品、新技术，不仅能够轻松赢得投资者的青睐，亦可以立刻俘获海量用户，但它的盛行也容易误导职场新人。应届大学生由于阅历的欠缺，很难判断哪些是真风口哪些是伪风口，而且许多风口转瞬即逝，急需获取经验的毕业生一旦踏错风口，就有可能错失黄金期。</span><br/><br/><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">不久前，人们还在热衷于谈论共享经济、O2O、甚至直播答题等所谓的风口，市场上充斥大量的热钱，创业公司层出不穷，现在，这些关键词已经淡出投资界，如果当时有毕业生恰巧选择了这些行业，现在的处境将异常尴尬。</span><br/><br/><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">相比之下，另有一些企业并不喜欢追逐热点，更倾向于稳扎稳打，逐渐成为某些细分领域的领导者，如特立独行的华为经过近30年的耕耘已经在全球通信产业拥有一定声誉，坚持核心科技的格力亦在竞争激烈的家电行业占据一席之地，他们从不关注热点，却长久地屹立于潮头。</span><br/><br/><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">这样的隐形小巨人既具有类似于校园的文化，又具有良好的发展前景，非常有利于应届大学毕业生顺利过渡，并迅速实现专业能力与人际交往能力的双重提高，职场新人应该将多关注这种类型的招聘单位。</span><br/><br/><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">拥抱大公司 VS. 追求“小而美”？</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">加入一家大公司，这不仅意味着你将拥有良好的福利待遇，还意味着自我介绍时会收获众人的羡慕眼神，但大企业的每个岗位都有明确的岗位职责，一个萝卜一个坑，适合比较循规蹈矩的人选。</span><br/><br/><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">而“小而美”在制度建设方面大多不够完善，员工需承担多方面职责，如果你希望更好地发挥自己的创造力，遇到这类公司尤其是兼具大公司与“小而美”双重优点的招聘单位一定不要放过。</span><br/><br/><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">盯着钱袋子 VS. 放眼明天？</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">许多毕业生找工作都非常关心工作待遇，对此我们表示理解。目前国内尤其是一线城市生活成本急剧上涨，每个人都面临巨大的生存压力，如果没有一定的物质回报，大家很难全身心投入到工作之中。不过，建议大家能够既要看到眼前的又要放眼明天。</span><br/><br/><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">由于每位应届生都是一张白纸，除个别招聘岗位外，绝大多数的待遇都在一个大致区间内，收入差距将在你工作3-5年后拉开，而当你足够优秀时，在任何一家公司都可以享受到相同级别的待遇。因此，你更应该选择那些提供大量机会的工作单位。</span><br/><br/><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">迈好职场第一步对于每一位毕业生都至关重要。在职场，第一份工作直接影响了每一位毕业生未来的职业走向和职业发展。</span><br/><br/><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">希望，每一位即将离校的天之骄子们都能在这个毕业季早日获得一份理想的Offer</span></p><p><br/></p>', 0, '发布', 1594879613, NULL);
INSERT INTO `job_news` VALUES (19, '职场要理性，不要任性', '职场快讯', '<p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; font-family: PingHei, &quot;color:#333333;font-size:16px;text-align:justify;background-color:#FFFFFF;&quot;;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">朋友小洁是一位职场新人，前段时间和我埋怨说，她的直属领导离职了，新的领导已经走马上任。新领导来到她们部门之后出台了一系列部门规章制度和方案流程，这些新的措施让她内心很排斥、很压抑，她从心底不接受跟着新领导共事，并且准备离职。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; font-family: PingHei, &quot;color:#333333;font-size:16px;text-align:justify;background-color:#FFFFFF;&quot;;\"><br/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; font-family: PingHei, &quot;color:#333333;font-size:16px;text-align:justify;background-color:#FFFFFF;&quot;;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp; &nbsp; &nbsp; &nbsp; 我劝她说，职场中要理性客观的看待人和事，不能任性到随意讨厌一位领导甚至不配合她（他）工作。每一位领导都有个人独特的管理方式和经营模式，应该理性的去接受，要去学习其亮点和特色，而不是急于离职，成熟的职场人不到万不得已不会轻易离职。</span></p><div style=\"border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal; text-align: center;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"></span><img src=\"https://pic1.58cdn.com.cn/nowater/58toutiao/big/n_v2951be4b1b6754979b5b87bde0068ab1f.jpg\" alt=\"èåºè¦çæ§ï¼ä¸è¦ä»»æ§\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; vertical-align: top;\"/><span style=\"margin: 0px; padding: 0px; border: 0px;\"></span><span style=\"margin: 0px; padding: 0px; border: 0px;\"></span><br/></div><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; font-family: PingHei, &quot;color:#333333;font-size:16px;text-align:justify;background-color:#FFFFFF;&quot;;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp;好在小洁后来想通了，昨天和我通电话说，其实以前反感这位新领导有些无理取闹了，事实证明新领导的很多决策对她们销售业绩起到了立竿见影的效果。而之前与其说小洁反感新领导，不如说是打心底里不习惯，当这位新领导带着她们团队创造一次次傲人业绩时，小洁也开始发自肺腑的佩服她的新领导。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; font-family: PingHei, &quot;color:#333333;font-size:16px;text-align:justify;background-color:#FFFFFF;&quot;;\"><br/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; font-family: PingHei, &quot;color:#333333;font-size:16px;text-align:justify;background-color:#FFFFFF;&quot;;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp; &nbsp; &nbsp; &nbsp; 霍金说过：“不管你的境遇如何，你只能全力以赴。”在职场中无论职场人遇到怎样的状况，请正视而不是选择逃避，因为只有客观的面对才能找到正确的方向。像小洁如果当初轻易离职了，她就不会迎来后面的傲人业绩。有时候职场人除了做好手头工作还要耐住性子，选择离开某种程度上只是为自己开脱，没有从根本解决问题，在职场路上只要按部就班迈着稳健的步伐，总会等到胜利的果实。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; font-family: PingHei, &quot;color:#333333;font-size:16px;text-align:justify;background-color:#FFFFFF;&quot;;\"><br/></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(51, 51, 51); font-size: 14px; white-space: normal; font-family: PingHei, &quot;color:#333333;font-size:16px;text-align:justify;background-color:#FFFFFF;&quot;;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-family: PingHei, &quot;font-size:14px;background-color:#FFFFFF;&quot;;\">职场中，本来就没有完全适合某个人的工作或事业，因为现在大部分人根本不知道自己适合什么，不管工作还是生活，不要因为短暂的压抑而断送了自己发展的前景和平台。愿职场中的你，始终心怀善良，努力追逐自己的梦想，扛起属于自己的责任。每天携满满的正能量去拥抱自己的事业和未来，面对职场中遇到的困惑时，能够理性而不是任性的去处理。</span></p><p><br/></p>', 0, '发布', 1594879633, NULL);
INSERT INTO `job_news` VALUES (20, 'Evernote的重大革新：取代电子邮件和PPT', '职场快讯', '<div class=\"content_con_type\" style=\"padding-top: 20px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">在旧金山的 EC4 大会上，Evernote 宣布了重大更新，全新设计的网页客户端和针对企业，提高生产效率的聊天平台&amp;ldquo;Work&nbsp; Chat&amp;rdquo;，以及更优化的演示功能，还有智能化的写作相关内容推送 Context。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">对于这些改进的背后逻辑，Evernote CEO Phil Libin&nbsp; 表示，他们正在尝试重新思考如何去构筑应用和服务，在他看来，如今我们的工作主要由四个要素构成：写作、收集、寻找和演示。为此，他们试图去满足这四点需求。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">在 Evernote 的应用中，全新的两个特性是 Context 和 Work Chat。Context&nbsp; 的功能在于，当用户输入新的文本时，文本界面将会自动出现相关性的信息，比如和当前文本中正在编辑某个人的名字相关的 Linkedin 信息，比如我这篇正在写的&nbsp; Evernote 文章，旁边可能会展现的是来自《华尔街日报》或者《连线》网页上的相关新闻，还有我之前和 Evernote 相关的笔记。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">也就是说，Context 功能背后其实是有文本理解能力，数据检索和匹配能力的。不过 Context&nbsp; 的智能程度如何，会不会对用户造成干扰还有待时间检验。而在会上，Evernote 也表示，Linkedin 和 WSJ 成为了 Evernote&nbsp; 的官方合作伙伴。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">Work Chat 顾名思义，就是用于工作中的聊天功能。主打的应该是保持沟通，但不影响生产效率。对于这点，我想许多人都有被 QQ&nbsp; 或微信不断出现的新消息干扰的经历。Work Chat&nbsp; 最佳的使用场景为，当多人正在同一时间查看一个共享文档时，多人就可以进行聊天，很明显，这个特性是为了方便协作而生，在聊天中，人们可以分享信息、网页链接，之前的笔记等内容。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">这些新的特性将会于今年年底到来，iOS、Android、Mac 和网页端的 Evernote 都可以得到更新。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">同时，Evernote 还推出了重新设计的网页版 Evernote，以及新的 Evernote 演示模式，后者可以将 Evernote&nbsp; 中已经存在的笔记、照片、地图等信息转换成 PPT 式的演示文稿。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\"></span></div><div class=\"content_page\" style=\"margin-top: 50px; padding-top: 30px; border-width: 1px 0px 0px; border-top-style: solid; border-right-style: initial; border-bottom-style: initial; border-left-style: initial; border-top-color: rgb(221, 221, 221); border-right-color: initial; border-bottom-color: initial; border-left-color: initial; border-image: initial; width: 1200px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"></div><p><br/></p>', 10, '发布', 1594879648, NULL);
INSERT INTO `job_news` VALUES (21, '快递行业详解：为什么快递员招聘比较难？', '职场快讯', '<div class=\"content_tit_type\" style=\"border: 0px; width: 1200px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><ul style=\"border-width: 0px 0px 1px; border-top-style: initial; border-right-style: initial; border-bottom-style: dashed; border-left-style: initial; border-top-color: initial; border-right-color: initial; border-bottom-color: rgb(221, 221, 221); border-left-color: initial; border-image: initial; list-style: none; width: 1200px; text-align: center; font-size: 0px; height: 40px;\"><li style=\"padding-right: 35px; padding-left: 35px; border-width: 0px 1px 0px 0px; border-top-style: initial; border-right-style: solid; border-bottom-style: initial; border-left-style: initial; border-top-color: initial; border-right-color: rgb(221, 221, 221); border-bottom-color: initial; border-left-color: initial; border-image: initial; display: inline-block; height: 12px; line-height: 12px; font-size: 12px; color: rgb(136, 136, 136);\">发布时间：2018-10-05 16:51:51</li><li>&nbsp;</li><li style=\"padding-right: 35px; padding-left: 35px; border: none; display: inline-block; height: 12px; line-height: 12px; font-size: 12px; color: rgb(136, 136, 136);\">浏览次数：211</li></ul></div><div class=\"content_con_type\" style=\"padding-top: 20px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">如今已经进入了快递行业的繁忙期，为了稳定度过这段受人瞩目的时期，各大快递公司和快递网点开始抢人大战，因为只有快递员跟上进度，这些快递服务企业才不会受到抱怨。但是，在实际招人过程中，快递企业表示，尽管已经开出了很诱人的条件，但是招聘工作依然比较困难。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">问题来了，为什么快递员招聘比较难?</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">原因1：审核标准拉低快递员所得</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">在工作人员的实地走访之后，我们发现，大部分快递公司的招聘启事上都会写，月平均工资能够达到5000左右。这个数额，一些打工者还是比较满意的，但是快递员能否真正拿到这部分钱，则有待商榷。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">每个快递公司对快递员的工作有不同的审核标准，比如一天之内必须完成分派的派件量，否则底薪将会被扣;比如如果有客户进行投诉，大部分快递员都可能受连累;还有快递企业对快递员有更多的工作要求，如果有一项不能达标，快递员的劳动成果将无法全部达到他们的手里。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">原因2：职位本身不稳定风险高</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">快递员所得基本上都是采取计件取酬，多劳多得的收入模式，在这种引导下，快递员的收入差异较大，快递业务繁荣地区有的快递员月薪能够轻松上万，边缘地区，有的只能糊口，加上电商造节，引发“暴饮暴食”消费高峰，对快递员的工作虽然带来了业务量，但是对他们的体力上也是严重的挑战。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">因此，不管是从快递员收入的差异性，还是他们业务量变化，都隐藏着快递员可持续性和不稳定性的风险要高于其它职位，因此对于很多从业者而言，这份工作依然不是最佳选择。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">原因3：体力要求非一般职位可比</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">快递员本身需要东奔西跑，所以体力要求是第一位。因此，在竞争中，年轻体力好很占优势，反之年龄稍大的从业人员也会自动避免这个工作，除此之外，快递员的休息时间比较少，如果想要多挣钱，节假日更加需要付出比平时更多的劳动，所以这种高强度的工作，也让很多求职者望而却步。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">所以说，在选择快递员这个工作之前，这三点因素需要仔细考虑清楚，先了解这个行业，才能更好的去适应。盲目的去体验，如果落差过大，会适得其反的。</span></div>', 10, '发布', 1594879670, NULL);
INSERT INTO `job_news` VALUES (22, '面试不通过，为什么用人单位不会通知？', '面试宝典', '<div class=\"content_con_type\" style=\"padding-top: 20px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">这个问题还蛮有代表性，不止一个人询问过我，我就说说我对这个问题的理解吧，谨供参考：</span><br/><br/><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">一般来说，大部分的企业确实不会给面试未通过的候选人发通知，不过也要分招聘渠道，一般情况对于内部推荐渠道和猎头渠道推荐的候选人基本上都还是会给回复的，只是对于通过网站等途径自己投递简历的候选人，往往被拒后不会再另行通知。当然这也算不上什么行业惯例，毕竟每个公司都可以有自己不同的方式和做法，有时企业负责人或者HR负责人、招聘负责人的理念也会对这事产生不同的影响，所以在这件事上还真没有统一的标准。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><br/></span></p><br/><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">但是，我想澄清一点，不通知，并非对候选人不尊重，更不是HR不专业，作为一个十几年招聘经验的HR，从实际工作的角度来看，之所以不通知，原因有以下几点：</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; text-align: center;\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><img src=\"http://www.jxwlrl.com/up/pic/201812/04/201812041019551832205464.jpg\" alt=\"\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; vertical-align: top;\"/><br/></span></p><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">逐个通知的成本太高</strong></span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">据统计过平均每个职位，约有50-100人投递简历(如果是校园招聘职位那就更多了)，进入初面的约有8-10人，进入复试的约有3-5人，最后录取的只有1-2人，如果每个都要回复的话，工作量无疑是很大的，即使是通过标准的邮件模板或短信模板通知，这个工作量也是不小的，工作量的背后就是时间成本和人工成本啊，这个企业当然要考虑的;</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">不好通知</strong></span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">中国的文化里是比较注重面子的，拒绝别人并不是一件“好事”，对于做这样的事情，大家在心里上天然是有一些排斥的，所以很多HR和面试官都不愿意做，除非公司硬性规定，否则还真没有几个人主动去给候选人发拒信的，发一个两个不难，难的是给每一个被拒的候选人都发，这个还真不是一般人能坚持的;</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">并不是所有人都愿意收到拒信的</strong></span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">对于收到拒信的人来说，他们真的会开心吗?真的每个人都愿意收到拒信吗?不见得吧。曾有公司尝试过给被拒的候选人发送拒信 “很遗憾的通知你，未能通过我司的面试 ”，结果呢，就有候选人认为这是在伤口上撒盐，回复了一堆很难听的话;有候选人回信询问面试未通过的原因，希望得到进一步的指导;还有候选人对自己未能应聘成功表示非常的不理解甚至是愤怒，要求公司给个说法，要面试官的联系方式，要求重新面试等等;总之，林子大了，什么样的人都有。</span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">总有意外发生</strong></span><br/><br/><span style=\"margin: 0px; padding: 0px; border: 0px;\">职场上经常出现已经发了offer的候选人在入职前放鸽子，也有入职没几天就离职的情况发生，面对各种不确定的情形，保险的做法，当然是不给未被选中的候选人明确的答复，因为有些候选人虽然不是那个最合适的，但是也很不错，完全可以作为备选，这样，一旦选定的 候选人不能入职，企业至少还有其他候选人可用啊，说的难听点，留个备胎嘛，如果急匆匆的把拒信都发出去了，一旦出现意外情况怎么办?顺位的其他候选人都被拒了，连个备选的都没了，这不是给自己挖坑吗?</span></div>', 20, '发布', 1594879707, NULL);
INSERT INTO `job_news` VALUES (23, '毕业生求职：换位思考 助你成功', '面试宝典', '<div class=\"content_con_type\" style=\"padding-top: 20px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">某公司连续多年招聘的大学生都在500人以上，最多的一年是1000人，每年招聘我都会去现场，除了大家所提出的体会外，我还有一个体会就是身体累，北京、成都、西安、武汉、东三省、山东、河北甚至新疆等地方安排的非常紧张，一站挨着一站，还要协调学校、公司领导、后方校园招聘支持小组等等，确实需要体力。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">校园招聘的方案、流程等等我之前有过简要说明，这次主要站在面试官这个角度给大学生们提一些我自己的建议，需要在哪些方面注意和加强，给大家指出一些思路和方向，帮助大家能够顺利找到自己满意的工作，供大家（包括大一至大四，还有研究生等）参考。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">以下这些方面的排序不分先后，按照我的随机思路排序：</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">一、学校推荐</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">正规的校园招聘一般都会有一个程序，就是用人单位到达学校之后，会学校负责就业的部门（可能是别的部门）进行会见，就本站招聘的具体工作进行商讨。这个过程中，如果有学校能够给用人单位一个名单，作为重点推荐，那么我们也就会在面试的时候格外注意，甚至会给一些照顾。你如果能够在这份名单里面，那你就比别人抢先了一步。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">二、宣讲表现</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">用人单位宣讲过程中，会有很多表现的机会。比如提问环节，你如果认真听讲，并能够在提问环节得到提问机会，问出一些令面试官满意的问题来，引起面试官的注意，那么你就会在面试环节多一些机会。当然，如果你没有提问，也是有机会的，比如在组织宣讲过程中主动维持秩序、帮助操作学校的电脑、投影仪，宣讲结束主动问一些“专业”问题，帮宣讲人员指路、拿物品等等，这个需要机会和眼色。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">三、简历制作</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">同样的条件，不同的排版、不同的文字描述等，都会使简历差异化，一份能让面试官眼前一亮的简历无疑会增大你面试的成功率。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">四、学习成绩</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">学习成绩是很重要的一个内容，因为学生的本职工作就是学习，学习成绩没有搞起来，其他也不好说。学习成绩不仅包含绩点，同时还要包含外语成绩。如果不是计算机相关专业，计算机相关证书用处并不大。笔试环节也需要比较好的成绩做基础。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">五、学生职务</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">如果你是校学生会主席，那恭喜你，如果没有什么致命缺陷，你找工作会容易很多。当然院系学生会主席、团支部书记等职务也都非常不错。一些好的协会会长有的单位也会考虑。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">六、特殊才艺</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">有些单位在招聘的时候领导给招聘负责人说，有某某特长的给我留意留意。A单位在本工业园区的篮球比赛中总是失利，那么你篮球打的好，就有可能被录用。同理：足球、乒乓球、舞蹈、写作甚至歌唱水平等等都有可能帮你成功。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">七、谈吐气质</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">你不要否认，有很多面试官都是来自外貌协会的，我也不例外。面试的时候穿着随意、发型不整、讲话没有逻辑、举止行为没有礼貌、唯唯诺诺、趾高气扬等等，都会阻碍你面试步伐。其中一些能力和素质，还要尽早练习，临时抱佛脚不可取。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">八、主动争取</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">面试官不是伯乐，也许你是千里马，但是漏网了，那怎么办？主动去争取。我曾经遇到好好几个这样的学生：第二轮面试结束，我在等车回酒店的2分钟内，来了一个学生，非常有礼貌，非常有条理地讲了自己的情况，希望争取能到第三轮面试，打动了我，于是我给他增加了一个名额。还有一个学生，条件很优秀，最后一轮没有通过，在我第二天一大早准备离开这个城市的时候，他却已经安静的等在门口不远处（我的房间号都是很保密的，只有个别少数老师知道，连司机都不知道），经过一番对话，他也多了一个参加最终筛选机会。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">九、身体素质</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">包括我们单位在内的很多单位都在面试环节增加了身体素质考核的项目：如1000米、立定跳远、跳绳、仰卧起坐、引体向上等，如果你身体素质跟不上，不好意思，不考虑。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">最近心态不好，思绪有些乱，再强调一遍，以上不分先后，也没有必要都努力做到，不同的企业不同的岗位要求也是不一样的，有时候你做到其中一个很优秀可能也就够用了。如果你有目标单位，建议多去了解一下目标单位的情况，做到有的放矢，提升成功命中率。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">校招过程中有很多有意义的事情，也是我人生履历的一个组成部分。</span></p></div>', 0, '发布', 1594879736, NULL);
INSERT INTO `job_news` VALUES (24, '求职者“利用”面试官 促成就业！', '面试宝典', '<div class=\"content_con_type\" style=\"padding-top: 20px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">你只感受到了失望的情绪，这种情绪对你来说毫无价值。你应该想想能从面试官身上拿到什么其他的收获。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">最直接的方法就是去问那个淘汰掉你的面试官，看不上你哪里。放下高冷，通过一次次面试收集关于他人对你的印象信息，帮助你提高和完善自己，争取在下一次面试中表现得更好一些。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">或许你是个不错的候选人，只是谈话时的一些小动作让面试官不喜，或许雇主希望你能拥有一定的技能组合，比如熟练photoshop的同时懂一些Edius、Premiere等视频编辑技巧。又或许你的不足之处仅仅是HR认为你无法适应公司文化。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">吃一堑长一智，在不断的磨砺中辨别各类面试官，将自己调整到最恰当的状态去面对他们。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">通常来自面试官的反馈会非常直白且犀利。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">比如，坐在那里下意识地不断抖腿，或者每次回答前先重复一遍提问，这些让人讨厌的习惯可能你自己都没发现。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">又或许你重复面试官的问题只是想表达对其问题的关注，却容易造成回答问题时思路缓慢的误解。这些都需要由对方来启发你。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">两年前罗纹去一家公司面试，面试官是一位女性总监。整个面试只持续了五分钟，对方只问了罗纹关于这家公司有什么想了解的，然后就让她回去等消息。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">很显然面试失败了，罗纹临走时忍不住问面试官自己为什么被淘汰，得到的回答只是裙子太短，“如果面试你的人是男性，穿超短裙来面试容易让人误会你的动机”。走出这家公司办公楼，罗纹直接去买了件刚刚过膝的职业装。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">索问原因也要掌握相关技巧。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">作为一场考试的出题者，面试官给你的反馈是最有价值的。为此你可以当面或者用邮件告诉求职单位，你向往对方的公司，喜欢他们的团队，即使面试失败也希望能更多地了解他们的文化。还可以直接告诉面试官你需要不断完善，希望他的洞察力能给予你有价值的专业指导，并期待在未来成为公司一员。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">参加百度招聘的求职者多次面试同一个部门、或不断面试多个部门都是很常见的事，甚至有的人会经历十几轮面试之后拿到Offer。在这个漫长的过程中，求职技巧也需要被逐步磨练出来。</span></p></div>', 0, '发布', 1594879755, NULL);
INSERT INTO `job_news` VALUES (25, '2年以下工作经验求职者应聘攻略', '面试宝典', '<div class=\"content_con_type\" style=\"padding-top: 20px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">不过，事在人为。条件不好，咱就创造好的加分项。通过一些求职技巧，扬长避短，将自己最佳的一面推销出去，从而给不足的经验加分。<br/>　　2年以下工作经验，正在找工作的看过来，下面分享的这些应聘攻略，对你们很重要。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">应聘攻略<br/></strong>　<strong style=\"margin: 0px; padding: 0px; border: 0px;\">　·对岗位要“先下手为强”。<br/></strong>　　岗位就在那里，早起的鸟儿才有虫吃。三、四月是招聘旺季，人多竞争压力大，有很多优秀企业的招聘计划人数有限，招满后一般不会扩招，建议求职者要认真对待每次招聘会，在积累应聘经验的同时，抓住机会，找到适合自己的工作。<br/>　<strong style=\"margin: 0px; padding: 0px; border: 0px;\">　·突出可塑性<br/></strong>　　现在不少企业都有入职培训，所以不必为了没有工作经历而烦恼，可塑性和学习能力强也是职场半新人的法宝之一。面对招聘企业，可以陈述自己作为年轻人的优势，自信地面对提问，说不定就会将心仪的工作收入囊中。<br/>　<strong style=\"margin: 0px; padding: 0px; border: 0px;\">　·简历突出岗位技能<br/></strong>　　与应届生不同的是，职场半新人，因为对职场有一定的涉足，在工作独立性方面还是有一些优势。因此，在简历写作的时候，最好能罗列一下你在前一份工作中的具体任务。比如：主要做什么？能独立做什么？做出了哪些成绩？学习到了哪些新技能？</span></p></div>', 0, '发布', 1594879775, NULL);
INSERT INTO `job_news` VALUES (26, '求职面试怎么展开良好的自我介绍', '面试宝典', '<div class=\"content_con_type\" style=\"padding-top: 20px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">1.自我认识</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">想一矢中的，首先必须认清自我，一定要弄清三个问题。你是干什么的？你要干什么？你将来是要干什么的？你过去是干什么的？</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">2、投其所好</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">清楚自己的强项后，便可以开始准备自我介绍的内容：包括工作模式、优点、技能，、突出成就、专业知识、学术背景等。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">3、铺排次序</strong></span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">内容的次序亦极重要，是否能抓住听众的注意力，全在于事件的编排方式。所以排在头位的，应是你最想他记得的事情。而这些事情，一般都是你最得意之作。与此同时，可呈上一些有关的作品或增加纪录印象分。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">求职个人自我介绍是面试实战非常关键的一步，因为众所周知的“前因效应”的影响，你这2-3分钟见面前的自我介绍将在很大程度上决定你在各位考官心里的形象。这份介绍将是你所有工作成绩与为人处世的总结，也是你接下来面试的基调，考官将基于你的材料与介绍进行提问。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">个人单独面试基本上都是从开场问候开始，开场问候很重要，它有可能决定面试的基调。开场问候是给面试考官的第一印象，从言谈举止到穿着打扮将直接影响到你被录取的机会。进门应该面带微笑，但不要谄媚。话不要多，称呼一声“老师好”就足够，声音要足够洪亮，底气要足，语速自然，总之彬彬有礼而大方得体，不要过分殷勤，也不要拘谨或过分谦让。</span></p><p style=\"margin: 0px auto; border: 0px; line-height: 30px; text-indent: 2em; color: rgb(42, 42, 42); font-family: Simsun; background-color: rgb(245, 250, 255);\"><span style=\"margin: 0px; padding: 0px; border: 0px;\">接下来自我介绍；面试中一般都会要求考生先做简单的自我介绍，自我介绍的时间一般为2-3分钟左右。自我介绍是良好的表现机会，应把握以下一个要点：首先，要突出个人的优点和特长，并要有相当的可信度。特别是具有实际管理经验的要突出自己在管理方面的优势，最好是通过做过什么项目这样的方式来验证一下；其次，要展示个性，使个人形象鲜明，可以适当引用别人的言论，如老师、朋友等的评论来支持自己的描述；第三，不可夸张，坚持以事实说话，少用虚词、感叹词之类；最后，要符合常规，介绍的内容和层次应合理、有序地展开。要符合逻辑，介绍时应层次分明、重点突出，使自己的优势很自然地显露，不要一上来就急于罗列自己的优点。</span></p></div>', 10, '发布', 1594879793, NULL);
INSERT INTO `job_news` VALUES (27, '南昌航空大学自考综合试点改革专科、本科招生简章', '招生简章', '<div class=\"content_con_type\" style=\"padding-top: 20px; border: 0px; color: rgb(51, 51, 51); font-family: &quot;Microsoft YaHei&quot;, Arial, Helvetica, sans-serif, 宋体; font-size: 14px; white-space: normal;\"><div class=\"act_desc\" style=\"margin-top: 10px; margin-right: auto; margin-left: auto; padding: 10px; border: 1px solid rgb(205, 221, 240); font-family: 宋体; background: rgb(243, 247, 252); color: rgb(103, 103, 103);\">南昌航空大学自考综合试点改革专科，本科：考试时间为4、5、10、11月份，一年即可考完。南昌航空大学主考，校内考试，校内上课</div><div class=\"act_neirong\" style=\"margin-top: 10px; border: 0px; font-family: 宋体; color: rgb(47, 47, 47);\"><div style=\"border: 0px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp;<img alt=\"\" src=\"http://www.jxnhzk.com/uploads/allimg/130519/1-130519194011a2.jpg\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; vertical-align: top; height: 596px; width: 591px;\"/></strong></span></div><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">&nbsp; &nbsp;<br/>&nbsp; &nbsp;<span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(0, 0, 0);\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 18px;\">南昌航空大学是江西省最早开展自考助学教育的院校，是省教育考试院确定的自学考试主考院校，是省自学考试工作先进单位，是首批也是我省唯一被国家考委批准为《全国示范性学习服务中心试点建设单位》的公办院校。经江西省自学考试委员会批准，</span></span></strong></span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(0, 0, 0);\"><span style=\"margin: 0px; padding: 0px; border: 0px; line-height: 20px; font-size: 16px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">学校承担5个试点专科专业</strong></span><strong style=\"margin: 0px; padding: 0px; border: 0px;\">、</strong><span style=\"margin: 0px; padding: 0px; border: 0px; line-height: 20px; font-size: 16px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">本科13个试点专业的主考任务</strong></span></span><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\"><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(0, 0, 0);\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 18px;\">。</span></span><br/>&nbsp;<br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\">专科招生专业</span></strong>：<span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 153);\">工商企业管理</span></span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 153);\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\">，会计，工程造价管理，市场营销学，</span></span><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 153);\">新闻学</span></span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 204); font-size: 16px;\">。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">本科招生专业</strong></span>：<span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 204);\">工商企业管理</span></span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 204); font-size: 16px;\">，会计</span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 204); font-size: 16px;\">，</span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 204); font-size: 16px;\">工程造价管理，市场营销学</span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 204); font-size: 16px;\">，新闻学</span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 204); font-size: 16px;\">，法律</span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 204); font-size: 16px;\">，建筑工程</span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 204); font-size: 16px;\">，公共事业管理</span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 204); font-size: 16px;\">，</span><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 204);\">机械制造及自动化，数控技术，模具设计与制造，计算机及应用，动画设计。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">本科招生对象</strong></span>：大专在读及具有国民教育序列专科文凭毕业生。<br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">报名资料</strong></span>：携带二代身份证原件、专科毕业证复印件或学生证复印件。</span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(0, 0, 0); line-height: 20px; font-size: 16px;\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\"><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\">专科</span></strong>：只需携带</span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(0, 0, 0); line-height: 20px; font-size: 16px;\">二代身份证原件。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\">&nbsp;<br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">助学特色</strong></span>：<br/>1、&nbsp; 网学课程参加南昌航空大学高等教育自学考试学习服务中心开设的网络学习，注网络学习成绩占成绩的30%；其它课程考前均开设辅导班，精准讲解考试重点、难点。<br/>2、&nbsp; 根据江西省教育考试院相关政策，统招专科生可免部分公共课。<br/>3、&nbsp; 南昌航空大学主考，校内上课，校内考试，师资队伍强，教学质量高。<br/>4、&nbsp;&nbsp;<span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 204);\">一年考4次（4、5、10、11月），最多一次考6门，一年即可考完。</span><br/>&nbsp;<br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">学历证书</strong></span>：由江西省自考委员会和南昌航空大学共同颁发自学考试本科毕业证书；达到学位授予条件的，授予相应学科的学士学位。<br/>&nbsp;<br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">毕业证查询</strong></span>：中国高等教育学生信息网（www.chsi.com.cn）。<br/>&nbsp;<br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">成绩查询</strong></span>：江西省教育考试院网站（www.jxeea.cn）。<br/>&nbsp;<br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">毕业待遇</strong></span>：<br/>学生修满教学计划规定课程并考试合格，颁发南昌航空大学与江西省自考委员会联合署印的本科<br/>毕业证，符合学士学位条件者，可授予学士学位。所获文凭，国家承认，并在中国高等教育学生信息网查询。毕业生的工资待遇，晋级、参加加全国硕士研究生考试、公务员考试、司法考试以及就业资格证与普通高校同类专业毕业生的待遇同等。<br/>&nbsp;<br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">收费标准</strong></span>：<span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(204, 0, 204);\">学费5200元，报名时一次交清。教材费、报考费实践考核费、毕业论文答辩费等另行缴纳。</span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">报名地址</strong></span>：江西省南昌市上海路173号南昌航空大学继续教育学院101室。<br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">乘车路线</strong></span>：市区乘10、11路到辛家庵，207，1路到上海路华联超市站下。201、旅游1线到天香园站下车即可。<br/><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">咨询电话</strong></span><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(0, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">：<span style=\"margin: 0px; padding: 0px; border: 0px;\">512763522</span></strong></span><br/><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: 16px;\"><span style=\"margin: 0px; padding: 0px; border: 0px; color: rgb(255, 0, 0);\"><strong style=\"margin: 0px; padding: 0px; border: 0px;\">学院网址</strong></span>：<a href=\"http://undefined\" style=\"margin: 0px; padding: 0px; border: 0px; outline: none;\">http：//www.jxnhzk.com</a><br/>&nbsp;<br/><strong style=\"margin: 0px; padding: 0px; border: 0px;\">温馨提示</strong>：学费请直接到校缴纳，学院没有委托任何人在校内外代收任何费用，望广大学生和家长勿轻信他人，以防上当受骗（学生缴费后开具加盖南昌航空大学继续教育学院公章的收费单据）！</span></span></div></div>', 20, '发布', 1594879818, NULL);

-- ----------------------------
